-- ----------------------------
-- Table structure for `yun_news`
-- ----------------------------
DROP TABLE IF EXISTS `yun_news`;
CREATE TABLE `yun_news` (
  `NewsID` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `ColumnID` char(10) DEFAULT NULL,
  `NewsTitle` varchar(100) DEFAULT NULL COMMENT '标题',
  `NewsBrief` varchar(100) DEFAULT NULL,
  `ImagesUrl` varchar(50) NOT NULL,
  `Penster` varchar(50) DEFAULT NULL COMMENT '作者',
  `Content` text COMMENT '新闻内容',
  `clicked` int(11) DEFAULT '0' COMMENT '点击量',
  `PublishTime` int(11) DEFAULT NULL COMMENT '发布时间',
  PRIMARY KEY (`NewsID`)
) ENGINE=InnoDB AUTO_INCREMENT=63 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of yun_news
-- ----------------------------
INSERT INTO `yun_news` VALUES ('1', '4', 'iOS应用全球收入Top10揭晓：订阅应用霸榜，快手入围', 'Sensor Tower揭晓了过去一年全球收入最高的iPhone、iPad应用榜单，其中Netflix以7.9亿美元的收入位居榜首，其次则是腾讯视频的4.9亿美元。', '../upload/news/up_5c174b3e666c4.png', 'admin', '<p style=\"margin-top: 0px; margin-bottom: 0px; padding: 6px 15px 18px; border: 0px; font-family: Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">日前，businessinsider援引移动应用市场研究公司Sensor Tower报告揭晓了过去一年全球收入最高的<a class=\"keyword_link\" href=\"http://product.yesky.com/mobilephone/apple/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">iPhone</a>、<a class=\"keyword_link\" href=\"http://product.yesky.com/tabletpc/apple/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">iPad</a>应用榜单，其中Netflix以7.9亿美元的收入位居榜首，其次则是<a class=\"keyword_link\" href=\"http://mydown.yesky.com/soft/256/256790.shtml\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">腾讯视频</a>的4.9亿美元，然后依次是约会应用Tinder(4.622亿美元)、<a class=\"keyword_link\" href=\"http://mydown.yesky.com/soft/108/30174108.shtml\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">爱奇艺</a>(4.205亿美元)、<a class=\"keyword_link\" href=\"http://mydown.yesky.com/soft/127/33506127.shtml\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">快手</a>(2.645亿美元)。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 6px 15px 18px; border: 0px; font-family: Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">快手</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 6px 15px 18px; border: 0px; font-family: Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　6~10名则分别为YouTube(2.442亿美元)、Pandora(2.257亿美元)、<a class=\"keyword_link\" href=\"http://mydown.yesky.com/soft/network/download/217/440717.shtml\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">优酷</a>(1.929亿美元)、<a class=\"keyword_link\" href=\"http://mydown.yesky.com/soft/33/31000033.shtml\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">QQ</a>(1.597亿美元)、Hulu(1.326亿美元)。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 6px 15px 18px; border: 0px; font-family: Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　不难发现，<a class=\"keyword_link\" href=\"http://product.yesky.com/znsh/ios\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">iOS</a>应用收入排行榜前十名由订阅应用主导，比如Tinder提供付费版本Tinder Plus和Tinder Gold，腾讯视频、爱奇艺、优酷等也提供会员服务;</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 6px 15px 18px; border: 0px; font-family: Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 6px 15px 18px; border: 0px; font-family: Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　而且，iOS应用收入排行榜前十名<a class=\"keyword_link\" href=\"http://product.yesky.com/mobilephone/china\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">中国</a>产应用还占了5席，足以表明中国市场对<a class=\"keyword_link\" href=\"http://product.yesky.com/officesoftware/pingguo/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">苹果</a>App Store的影响力。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 6px 15px 18px; border: 0px; font-family: Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　值得一提的是，作为唯一一个进入“应用”类以及“所有类别”收入Top10的短视频平台，快手近两年持续受到年轻群体欢迎，旗下用户已接近7亿、月活用户超2.5亿。</p>', '2', '1545030462');
INSERT INTO `yun_news` VALUES ('2', '4', 'Edge浏览器已死？微软：新版本2019年推出，改用Chromium渲染引擎', '微软称，全新的基于Chromium的Edge浏览器预览版将在2019年年初推出，新浏览器的名称还是Microsoft Edge。', '', 'admin', '<p style=\"margin-top: 0px; margin-bottom: 0px; padding: 6px 15px 18px; border: 0px; font-family: Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"><a class=\"keyword_link\" href=\"http://product.yesky.com/notebookpc/win10\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">Windows 10</a>，可以说是<a class=\"keyword_link\" href=\"http://product.yesky.com/netsoftware/microsoft/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">微软</a><a class=\"keyword_link\" href=\"http://product.yesky.com/os/windows/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">Windows</a><a class=\"keyword_link\" href=\"http://product.yesky.com/os/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">操作系统</a>中增长最快的一个版本，目前已拥有超过7亿用户。但是，Windows 10系统<a class=\"keyword_link\" href=\"http://product.yesky.com/soundcard/in\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">内置</a>的默认浏览器<a class=\"keyword_link\" href=\"http://product.yesky.com/database/microsoft/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">Microsoft</a><a class=\"keyword_link\" href=\"http://product.yesky.com/wirelessnetworkcard/edge\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">&nbsp;Edge</a>却没有达到预期的效果，更有传闻称微软将抛弃Edge浏览器。难道这款微软花大量人力物力打造的Edge浏览器就这么落幕吗？</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 6px 15px 18px; border: 0px; font-family: Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　对此，微软表示“接下来一年左右的时间里，微软将对Microsoft Edge浏览器进行技术变革”。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 6px 15px 18px; border: 0px; font-family: Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">Microsoft Edge浏览器</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 6px 15px 18px; border: 0px; font-family: Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　具体而言，桌面版Microsoft Edge浏览器将采用Chromium渲染引擎(<a class=\"keyword_link\" href=\"http://product.yesky.com/saas/google/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">谷歌</a><a class=\"keyword_link\" href=\"http://mydown.yesky.com/desktop/gamedesk/manhuakatong/318/416318.shtml\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">Chrome</a>和移动版Edge浏览器同款)开源项目开发，而非自家的EdgeHTML渲染引擎。而全新的基于Chromium的Edge浏览器预览版将在2019年年初推出，新浏览器的名称还是Microsoft Edge。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 6px 15px 18px; border: 0px; font-family: Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　另外，全新的Microsoft Edge浏览器不仅将成为Windows系统的默认浏览器，<a class=\"keyword_link\" href=\"http://product.yesky.com/notebookpc/win7\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">Windows 7</a>/8/10用户都能使用;还会登陆<a class=\"keyword_link\" href=\"http://product.yesky.com/officesoftware/pingguo/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">苹果</a><a class=\"keyword_link\" href=\"http://product.yesky.com/notebookpc/mac\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">macOS</a>等平台，最早2019年实现。这也是微软与苹果M的又一次“合作”，2003年6月<a class=\"keyword_link\" href=\"http://product.yesky.com/os/mac/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">Mac</a>版Internet Explorer获得最后一次功能更新，<a class=\"keyword_link\" href=\"http://mydown.yesky.com/soft/network/browse/496/425996.shtml\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">Safari浏览器</a>正式发布前微软IE曾是Mac设备的默认浏览器。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 6px 15px 18px; border: 0px; font-family: Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　Microsoft Edge浏览器于2015年年初发布，3月底首个预览版推出，7月随Windows10系统正式面世，随后陆续推出<a class=\"keyword_link\" href=\"http://product.yesky.com/znsh/ios\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">iOS</a>和<a class=\"keyword_link\" href=\"http://product.yesky.com/znsh/android\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">Android</a>版本。NetMarketShare统计数据显示，2018年3月份微软Edge浏览器的市场份额为4.46%。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 6px 15px 18px; border: 0px; font-family: Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); text-align: left;\">浏览器市场份额</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 6px 15px 18px; border: 0px; font-family: Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　选用Chromium渲染引擎之后，Microsoft Edge浏览器的兼容性更好，还能减轻Web碎片化。不知，此举能否助力微软在浏览器市场扳回一局呢？</p>', '3', '1545030572');
INSERT INTO `yun_news` VALUES ('43', '4', '软博会', '由工业和信息化部、北京市人民政府共同主办的第二十二届中国国际软件博览会在北京展览馆开幕。', '../upload/news/up_5c174c3174113.jpg', 'admin', '<p style=\"margin-top: 0px; margin-bottom: 0px; padding: 6px 15px 18px; border: 0px; font-family: Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">2018年6月29日，由工业和信息化部、北京市人民政府共同主办的第二十二届<a class=\"keyword_link\" href=\"http://product.yesky.com/mobilephone/china\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">中国</a>国际软件博览会在北京展览馆开幕。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 6px 15px 18px; border: 0px; font-family: Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　工业和信息化部部长苗圩和北京市市长陈吉宁出席，并在首场全球软件产业发展高峰论坛上代表主办方致辞，工业和信息化部副部长陈肇雄主持高峰论坛，中国工程院院士、清华大学副校长尤政作题为“软件定义世界科技创造未来”的主旨报告，北京市副市长阴和俊出席了此次论坛。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 6px 15px 18px; border: 0px; font-family: Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　高峰论坛上，华为轮值董事长徐直军、中欧数字协会主席鲁乙己、<a class=\"keyword_link\" href=\"http://mydown.yesky.com/sjsoft/272/35340272.shtml\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">百度</a>高级副总裁王海峰、<a class=\"keyword_link\" href=\"http://product.yesky.com/database/ibm/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">IBM</a>大中华区董事长陈黎明、<a class=\"keyword_link\" href=\"http://product.yesky.com/financialsoftware/ufsoft/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">用友</a>网络董事长兼CEO王文京、字节跳动创始人兼CEO张一鸣，围绕中国软件产业发展、新时代下中欧软件合作新未来、软件智能开发、数字化商业下的企业服务等热点话题作专题演讲。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 6px 15px 18px; border: 0px; font-family: Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　第二十二届软博会以“新时代 新理念 新软件”为主题，作为软件和信息技术服务业领域规模最大，最具影响力，持续时间最长的软件专业展会。本届软博会设置了七展区，五峰会、一大赛、二十一场平行论坛以及软件之夜等系列活动。相较往年，今年进一步丰富展示形式，并在国际化、高端化和互动性方面提高水平，总体呈现八大特色。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 6px 15px 18px; border: 0px; font-family: Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　一，本届软博会共分为七大展区。第一展厅以区序厅以“软件产业迈向新<a class=\"keyword_link\" href=\"http://mydown.yesky.com/game/355/30970355.shtml\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">征程</a>”为主题，展示我国改革开放40年软件产业从无到有、从小到大的发展进程。第二展区以“软件科技创新”和“软件国际综合成果”为主题展示以北京市为代表的科技创新中心成果以及国内外知名企业的创新成果。第三展区以“软件前沿技术创新”为主题，体现创新驱动战略，展示云计算，大数据，物联网，人工智能，区块链，虚拟现实等技术产品。<a class=\"keyword_link\" href=\"http://product.yesky.com/battery/qtdc\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">其他</a>展区分别围绕 “软件支撑强<a class=\"keyword_link\" href=\"http://mydown.yesky.com/game/240/35288740.shtml\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">国战</a>略”、“软件贡献中国方案”、“软件服务社会民生”、“软件赋能创新创业”等主题，充分展示软件产业最新发展成果。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 6px 15px 18px; border: 0px; font-family: Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　二，全球软件产业发展高峰论坛主题鲜明、大咖云集、国际化程度高。为期两天半的高峰论坛将围绕“新时代新理念 新软件”、“数字经济引领智慧未来”、“工业互联 智造未来”、“聚焦人工智能，畅享美好未来”、“大数据赋能实体经济”等热点主题展开。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 6px 15px 18px; border: 0px; font-family: Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　三，首次举办工业互联网安全精英邀请赛。为分享和展示工业互联网安全最新技术进展，挖掘工业互联网安全优秀团队及人才，2018软博会将由国家工业信息安全发展研究中心牵头，举办“工业互联网安全精英邀请赛”。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 6px 15px 18px; border: 0px; font-family: Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　四，举办内容丰富的专题平行论坛。论坛内容围绕软件百强发布、区块链、云服务、人工智能、“一带一路”和知识产权保护等话题展开，并以主题演讲、圆桌论坛等多形式呈现。其中，新一代信息技术产业国际合作论坛、物联网创新与融合国际论坛将邀请到<a class=\"keyword_link\" href=\"http://product.yesky.com/mobilephone/usa\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">美国</a>、<a class=\"keyword_link\" href=\"http://product.yesky.com/aircondition/korea\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">韩国</a>、越南、泰国、葡萄牙等国际专家学者。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 6px 15px 18px; border: 0px; font-family: Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　五，升级打造软博会logo和吉祥物。Logo图形以软博会的英文缩写“CISE”为主体，同时将原Logo和地平线等元素有机融合，突出了软博会的行业属性和文化内涵。吉祥物“软宝”以人工<a class=\"keyword_link\" href=\"http://product.yesky.com/znjqr/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">智能机器人</a>为主体，较大比例的头部代表着极强的运算能力，闪电造型代表着数据高速传输，拥有亲切的无敌微笑，是所有人最贴心、最专业的软件小精灵。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 6px 15px 18px; border: 0px; font-family: Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　六，软博会多项推选结果将在“软件之夜”盛典公布。“软件之夜”是从上届软博会开始打造的特色板块。2018年软博会组织开展“优秀产品”、“优秀案例”展示活动，目前有20部作品从众多报名作品中脱颖而出，它们的结果将同2018工业互联网安全攻防精英邀请赛、2018软博会logo和吉祥物征集活动一起，在6月30日晚在“软件之夜”盛典宣布。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 6px 15px 18px; border: 0px; font-family: Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　七，升级改版“在线软博会”交易服务平台。2018年“在线软博会”交易服务平台利用虚拟现实技术打造线上展馆功能，实现了在线平台和线下展会的互联互通，在帮助企业宣传推广最新成果的同时方便用户观展，强化了软博会的展示功能。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 6px 15px 18px; border: 0px; font-family: Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　八，本届软博会还将举办多项权威成功发布活动。《数字中国：大数据时代》纪录片，软件和信息科技服务业大数据平台，工业和信息化蓝皮书等都会集中在此间与公众见面。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 6px 15px 18px; border: 0px; font-family: Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　第二十二届软博会大幕已经拉开，本届软博会主题和内容更加聚焦，在论坛设置、展览展示都集中体现了新时代软件产业发展的成绩，变化和未来发展<a class=\"keyword_link\" href=\"http://product.yesky.com/viruskillsoftware/trendnet/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">趋势</a>。同时，超过300家企业都将在北京展览馆充分展示最前沿的大数据、人工智能、物联网、区块链等技术，为广大用户呈现了一场丰富的科技盛宴。</p>', '2', '1545030705');
INSERT INTO `yun_news` VALUES ('44', '4', '微软悄然停止对使用老CPU Win7设备的安全支持', '据外媒 ZDNet 报道，微软悄然停止了部分使用老处理器的Windows 7设备的支持。报道指出，微软在Windows 7 2018年3月安全更新中列出了一个已知…', '../upload/news/up_5c174ca6ed4ac.jpg', 'admin', '<p style=\"margin-top: 0px; margin-bottom: 0px; padding: 6px 15px 18px; border: 0px; font-family: Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">据外媒 ZDNet 报道，<a class=\"keyword_link\" href=\"http://product.yesky.com/netsoftware/microsoft/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">微软</a>悄然停止了部分使用老处理器的<a class=\"keyword_link\" href=\"http://product.yesky.com/notebookpc/win7\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">Windows 7</a>设备的支持。报道指出，<a class=\"keyword_link\" href=\"http://product.yesky.com/product/793/793002/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">微软在Windows 7</a>&nbsp;2018年3月安全更新中列出了一个已知问题，微软表示那些不支持单指令多数据流式扩展的安全支持(Streaming Single Instructions Multiple Data Extensions 2，简称SSE2)的<a class=\"keyword_link\" href=\"http://product.yesky.com/gamessoftware/pc\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">PC</a>将出现一个“停止”错误。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 6px 15px 18px; border: 0px; font-family: Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　微软当时在“解决办法”一栏中提到，他们正在研究解决方案，后续会通过软件更新修复该问题。然而在最近微软推出的6月安全更新中，微软却要求用户更新处理器，或者是使用<a class=\"keyword_link\" href=\"http://mydown.yesky.com/soft/utilitie/systems/230/425230.shtml\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">虚拟机</a>。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 6px 15px 18px; border: 0px; font-family: Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　据悉，SSE2是2000年的产物，当时英特尔骄傲的向人们展示他们的最新<a class=\"keyword_link\" href=\"http://product.yesky.com/cpu/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">CPU</a>——<a class=\"keyword_link\" href=\"http://product.yesky.com/pc/pentium\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">奔腾</a>4。当年的分析师评论，奔腾<a class=\"keyword_link\" href=\"http://product.yesky.com/mobilephone/4xl/beidou/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">4系列</a>比奔腾<a class=\"keyword_link\" href=\"http://product.yesky.com/mouse/3series/rapoo/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">3系列</a>有显著的优势。奔腾4包含了一个改进的浮点单元和一套成为SSE2多媒体指令，这可以让芯片并行的处理多媒体，从而加速性能。SSE2则是一项对于英特尔处理器而言的高端功能。</p><p><br/></p>', '2', '1545030822');
INSERT INTO `yun_news` VALUES ('45', '4', '十年奋斗谱华章，天华星航业正芳', '2018年10月18日，天华星航品牌发布会暨十周年庆典在北京圆满落下帷幕。盛典已过一月有余，然而，重量级嘉宾', '../upload/news/up_5c17506d57464.png', 'admin', '<p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; font-family: 微软雅黑; font-size: medium; color: rgb(51, 51, 51); line-height: 26px; text-align: justify; white-space: normal;\">2018年10月18日，天华星航品牌发布会暨十周年庆典在北京圆满落下帷幕。盛典已过一月有余，然而，重量级嘉宾的精彩演讲，天华星航人的铮铮誓言，言犹在耳，印象深刻。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; font-family: 微软雅黑; font-size: medium; color: rgb(51, 51, 51); line-height: 26px; text-align: justify; white-space: normal;\">　　在发布会的分会场“技术沙龙”环节，天华星航存储产品线、大数据产品线、云计算产品线和自主可控产品线的带头人齐聚一堂，共忆天华星航十年发展历程，分享天华星航最新技术、产品和应用实践，充分展示了天华星航人“心怀责任，自强不息，众志成城，披荆斩棘”之精神，彰显了民族脊梁企业之风采。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; font-family: 微软雅黑; font-size: medium; color: rgb(102, 102, 102); line-height: 26px; text-align: center; white-space: normal;\"><img alt=\"\" src=\"/ueditor/php/upload/image/20181217/1545031760467196.png\" width=\"342\" height=\"228\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; font-family: 微软雅黑; font-size: medium; color: rgb(51, 51, 51); line-height: 26px; text-align: center; white-space: normal;\">　　天华星航品牌发布会暨十周年庆典技术沙龙现场</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; font-family: 微软雅黑; font-size: medium; color: rgb(51, 51, 51); line-height: 26px; text-align: justify; white-space: normal;\"><strong style=\"margin: 0px; padding: 0px;\">　　十年披荆斩棘，成就今日之辉煌</strong></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; font-family: 微软雅黑; font-size: medium; color: rgb(51, 51, 51); line-height: 26px; text-align: justify; white-space: normal;\">　　创立之初，天华星航即致力于自主产品的研发。十年砥砺，天华星航持续关注业内前沿知识，依据国际标准、法规及最佳实践要求，结合丰富的项目经验和对客户需求的深刻理解，不断调整自己的产品架构，持续提升产品性能。天华星航人用十年的辛勤和努力开创了“THE”系列民族品牌，用自己的行动改变了“洋品牌”一统天下的局面。十年积累，天华星航一步一个脚印，由单一的数据存储、备份产品，逐渐发展成为拥有数据存储、数据保护、云计算、安全与管理、大数据五大产品线、几十款自主研发产品。十年奋进，天华星航可为客户量身定制数据存储备份、容灾、高性能计算、<a class=\"keyword_link\" href=\"http://product.yesky.com/netsecurity/\" target=\"_blank\" style=\"margin: 0px; padding: 0px; color: rgb(51, 51, 51); text-decoration-line: none;\">网络安全</a>、数据优化管理、虚拟化、云计算、大数据和可视化等多方面的整体解决方案，提供贯穿企业信息系统生命周期的全方位服务。十年发展，天华星航已成为名副其实的高端IT综合服务商，成为民族企业的脊梁。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; font-family: 微软雅黑; font-size: medium; color: rgb(102, 102, 102); line-height: 26px; text-align: center; white-space: normal;\"><img alt=\"\" src=\"/ueditor/php/upload/image/20181217/1545031761413750.png\" width=\"468\" height=\"726\"/></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; font-family: 微软雅黑; font-size: medium; color: rgb(51, 51, 51); line-height: 26px; text-align: center; white-space: normal;\">知行合一，止于至善</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; font-family: 微软雅黑; font-size: medium; color: rgb(51, 51, 51); line-height: 26px; text-align: justify; white-space: normal;\">　　十年变迁，产品在与时俱进，持续更新，但天华星航人对产品质量的要求和追求极致的理念却始终如一，对打造高端民族品牌的信念从来不曾改变。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; font-family: 微软雅黑; font-size: medium; color: rgb(51, 51, 51); line-height: 26px; text-align: justify; white-space: normal;\"><strong style=\"margin: 0px; padding: 0px;\">　　一是追求极致的理念：知行合一，止于至善</strong></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; font-family: 微软雅黑; font-size: medium; color: rgb(51, 51, 51); line-height: 26px; text-align: justify; white-space: normal;\">　　北京天华星航科技有限公司历时十年打造的“THE”系列产品，能够满足客户的多层次需求，以高品质、高性价比为其提供完美的IT体验。信息安全无小事，天华星航的产品就如同企业的行事风格一般，稳字当头，质量可靠，性能优越，能够以贴心的设计满足客户的定制化需求。高质量和稳健产品的背后，是天华星航人的不懈努力与付出，是对“知行合一，止于至善”的极致追求，也是对“把复杂留给工程师，把简单留给客户”理念的完美诠释。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; font-family: 微软雅黑; font-size: medium; color: rgb(51, 51, 51); line-height: 26px; text-align: justify; white-space: normal;\"><strong style=\"margin: 0px; padding: 0px;\">　　二是紧随时代潮流：满足时代所需</strong></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; font-family: 微软雅黑; font-size: medium; color: rgb(51, 51, 51); line-height: 26px; text-align: justify; white-space: normal;\">　　天华星航与国内知名高校合作，持续关注国内外数据安全行业前沿技术，紧跟时代潮流。比如，云计算近几年才逐渐火热，但对于天华星航来说，它却不是新产物。天华星航在为客户服务、满足客户需求的过程中，很早就注意到云计算，并在云计算成为热宠之时，迅速推出自己成熟的THE Cloud自主品牌云计算产品，根据客户的使用场景来为客户精准配置，不断满足客户在信息化建设中的各种新需求。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; font-family: 微软雅黑; font-size: medium; color: rgb(51, 51, 51); line-height: 26px; text-align: justify; white-space: normal;\"><strong style=\"margin: 0px; padding: 0px;\">　　三是贯通的生态系统：多元产品只为你服务</strong></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; font-family: 微软雅黑; font-size: medium; color: rgb(51, 51, 51); line-height: 26px; text-align: justify; white-space: normal;\">　　天华星航目前拥有五大产品线、几十款产品，只为满足客户多样化需求。除了品类丰富的自主研发产品，天华星航还拥有经验丰富的技术人员，已为能源、军工、军队、交通、金融、科研院所、医疗、教育和媒体等多个国家重要行业提供了整体解决方案。同时，天华星航在华中、华东、华南、西南、西北和东北设立了分支机构，为客户提供更及时、高效的本地化服务。</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; font-family: 微软雅黑; font-size: medium; color: rgb(51, 51, 51); line-height: 26px; text-align: justify; white-space: normal;\"><strong style=\"margin: 0px; padding: 0px;\">　　四是博大的民族情怀：立足国内，走向世界</strong></p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; font-family: 微软雅黑; font-size: medium; color: rgb(51, 51, 51); line-height: 26px; text-align: justify; white-space: normal;\">　　“技术立足，服务为本，研发创新”是天华星航一贯秉承的企业宗旨，打造高端民族品牌的民族情怀已融入每个天华星航人的血液。“一带一路”政策的提出，使得天华星航更加坚定了“立足国内，走向世界”的步伐。积十载之经验，天华星航将充分发挥自身优势，凭借天华星航人自强不息的奋发精神，依托产品质量的自信，积极拓展新的发展渠道，实现产业转型、结构升级，打造具有国际视野的民族品牌，再创天华星航新的辉煌!</p><p style=\"margin-top: 16px; margin-bottom: 16px; padding: 0px; font-family: 微软雅黑; font-size: medium; color: rgb(51, 51, 51); line-height: 26px; text-align: justify; white-space: normal;\">　　盛典已落下帷幕，新的<a class=\"keyword_link\" href=\"http://mydown.yesky.com/game/355/30970355.shtml\" target=\"_blank\" style=\"margin: 0px; padding: 0px; color: rgb(51, 51, 51); text-decoration-line: none;\">征程</a>已然开始。在下一个十年，天华星航将立足IT基础架构建设，与时俱进，努力挖掘技术这一核心本质，以过硬的信息保护能力，持续为客户提供更好的产品和服务，致力成为客户最值得信赖的IT综合服务伙伴，为大数据时代的国家信息安全保驾护航!</p>', '1', '1545031789');
INSERT INTO `yun_news` VALUES ('46', '4', '大数据进入下半场 亿信华辰发力数据治理', '在当下市场变幻莫测、技术日新月异、业务大幅重构的大环境中，最重要的资产不再是产品、技术或者流程，毫无疑问是数据。', '../upload/news/up_5c1750b882497.png', 'admin', '<p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">在当下市场变幻莫测、技术日新月异、业务大幅重构的大环境中，最重要的资产不再是产品、技术或者流程，毫无疑问是数据。将数据转化为数字智能的能力是取得竞争优势的关键，而数据的准确性显得尤为重要，数据治理也越来越受到重视。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　而今，数据治理已经从国家层面开始积极建立制度标准，银行、证券等行业已成为数据治理的领先者。作为长期专注于数据全生命周期服务的提供商，亿信华辰结合多年的项目建设经验，为用户提供数据治理全栈解决方案和服务，深度挖掘数据价值，为领导决策提供支持。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"><strong>　　全过程与全方位的数据治理</strong></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　在大数据生态中可以分为采集、存储管理、应用三个阶段。在这个过程中传统数据治理也存在两个问题：第一，治理范围过窄，如果在应用过程中才发现数据的问题，那之前可能做的都是无用功，所以大数据治理要参与到整个过程中;第二，数据治理落地难，企业如果没有很好的技术平台数据治理是很难实现的，而且数据治理见效需要一定的时间。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　针对数据治理的难点，必须要对大数据全过程和全方位进行管控。在多年实践的基础上，亿信华辰今年推出一站式数据治理管理平台——睿治。以元数据为基础，将数据标准、数据质量、数据集成、主数据、数据资产、数据交换、生命周期、数据安全等9个方面的产品集成至整个服务环境中，9款产品可以相互调用，也可独立使用，为整个数据治理平台提供技术支撑与保障。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　亿信一站式数据治理管理平台是一款智能、敏捷的数据全生命周期的管理应用平台，打通数据治理各个环节，为企业保证了业务数据在采集、集中、转换、存储、应用整个过程中的完整性、准确性、一致性和时效性。平台具有五大特色：</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　第一，全行业。丰富的客户群体现了亿信的跨行业经验。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　第二，全体系。通过平台、服务、规划帮助企业把数据治理落地。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　第三，全框架。通过实践提炼出稳定的数据治理框架。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　第四，全自主。在数据治理平台的9大产品都是自主研发。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　第五，全过程。在数据的全生命周期进行治理。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"><strong>　　深耕行业定制解决方案</strong></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　随着数据的激增，遵守法规以及利用自由流动数据的重要性得以凸显出来。要想实现对数据的控制，如果不借助统一的数据治理平台、整合可信的数据分析战略，企业不仅可能被如潮的信息所淹没，还可能面临违反法规的风险。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　但大数据治理在每个行业都有不同的需求，针对特定行业要理解数据治理的行业需求。亿信华辰在银行、租赁、教育、卫生、政府等不同行业都有定制化方案。银行业注重建立银行数据资产监控平台和银行数据标准管理平台;租赁行业是基于全生命周期进行数据资产管控和运营;教育行业需要以治理为基础进行元数据管理平台;而政府要建立元数据驱动服务目录与数据交换体系。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　通过亿信华辰大数据团队多年来的大数据实施经验，建立了企业数据成熟度模型评估，为企业量身定做大数据实践路线，快速解决企业不同的数据治理场景。如今，亿信华辰已成功为一大批重要客户提供了数据相关服务，包括多个国家部级单位、<a class=\"keyword_link\" href=\"http://product.yesky.com/mobilephone/china\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">中国</a>进出口银行、中国农业发展银行、中国船舶工业集团公司、华润、中粮等用户。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　以中国农业发展银行为例，其以行长为核心建立了数据治理机构，数据治理由业务部门主导，IT部门执行，并且引入质量考核体系。亿信华辰为其构建出一体化的开发、调试、部署、运行和管理维护平台，规范数据的需求、设计、开发、集成、应用等环节，采集全行元数据，支撑了农发行数据标准落地、数据质量提升以及数据仓库的建设。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　结语：《孙子兵法》有云:“多算胜,少算不胜。”对于企业来说,若想实现“多算胜”,就必须掌握尽量多的数据,并获取足够强的数据管理能力;对于亿信华辰来说,则是在深入理解企业业务特点和需求的基础上,为更多行业提供包括咨询规划、数据分析、数据治理、数据挖掘等全方位的数据服务，真正“授之以渔”,帮助他们实现“多算胜”。</p>', '2', '1545031864');
INSERT INTO `yun_news` VALUES ('47', '4', 'Win10十月更新误删了文件数据，怎么办？微软官方承诺恢复', '日前在10月新发布会后，微软及时放出了Windows 10十月更新(版本号为1809)。虽然Windows 10十月更新带来了时间轴等新功能，但不少用户反映“安装更新后会误删文件”', '../upload/news/up_5c1751962ca02.png', 'admin', '<p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">日前在10月新发布会后，<a class=\"keyword_link\" href=\"http://product.yesky.com/gamessoftware/microsoft\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">微软</a>及时放出了<a class=\"keyword_link\" href=\"http://product.yesky.com/notebookpc/win10\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">Windows 10</a>十月更新(版本号为1809)。虽然Windows 10十月更新带来了时间轴等新功能，但不少用户反映“安装更新后会误删<a class=\"keyword_link\" href=\"http://product.yesky.com/scaner/wenjian\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">文件</a>”，随后微软第一时间撤下了Windows 10十月更新。显然，停止更新并不能解决这一问题，误删的那些文件如何修复呢？</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　对此，微软<a class=\"keyword_link\" href=\"http://product.yesky.com/os/windows\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">Windows</a>&nbsp;Insider计划负责人Dona Sarkar在Twitter上表示，微软将帮助因Windows 10 更新文件被误删的用户恢复相关数据，目前微软的技术支持团队已经准备好了修复工具，让用户的设备回到好的状态。如果用户不幸中招，只需联系本地客服或微软支持寻求帮助，即可找回丢失文件。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　微软称，更新故障导致数起数据遗失反馈，比例为0.01%。微软的Windows服务和交付项目管理主管约翰·凯布尔（John Cable）表示，“我们已经全面调查了所有数据丢失报告，确认及修复了更新中的所有已知问题，并进行了内部验证。”</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　不过微软官方也强调，如果用户手动检查了更新，并且确定更新后遇到遗失文件问题，请在问题解决前尽量减少使用受影响设备。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　微软方面还建议用户，不要尝试使用“立即更新按钮”或者从 Microsoft 软件下载中心使用媒体创建工具手动更新到Windows 10版本1809。请耐心等待，直到更新提供了<a class=\"keyword_link\" href=\"http://product.yesky.com/mainboard/intel\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">Intel</a>设备驱动程序。当阻止解除后，此页面将更新。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　如果你暂时还没有更新，还是耐心等待微软发布全新的Windows10更新吧。</p>', '2', '1545032086');
INSERT INTO `yun_news` VALUES ('48', '4', '360企业安全联手椒图科技发布服务器自防护产品', '9月5日，360企业安全集团与椒图科技携手宣布，360企业安全集团以8000万元入资椒图科技，同日推出服务器自防护产品。', '../upload/news/up_5c1751f8ab540.jpg', 'admin', '<p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">9月5日，360企业安全集团与椒图科技携手宣布，360企业安全集团以8000万元入资椒图科技，同日推出<a class=\"keyword_link\" href=\"http://product.yesky.com/service/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">服务器</a>自防护产品。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　针对政府、企业的服务器破坏事件越来越多，这给政府部门、企业造成了难以想象的损失，并且有常态化的趋势。椒图科技董事长陈明对此表示，鉴于目前众多服务器产品所面临的巨大安全威胁，360企业集团和椒图科技的联手合作将为用户打造从终端到服务器的全方位安全防护体系。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"><strong><em>椒图科技董事长陈明：双方将联手打造全方位安全防护体系。</em></strong></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　360企业安全集团总裁吴云坤认为，对抗网络黑产需要安全生态的能力协同，本次合作让双方从技术、销售渠道等多个层面，把各自优势结合在一起，为更<a class=\"keyword_link\" href=\"http://product.yesky.com/email/dyh\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">多用户</a>输出完整的安全能力。吴云坤对本次合作充满信心，他表示，“在整个信息化发展过程中，核心的很多东西都在服务器上，无论从信息化和攻防两个维度来看，我们都看到服务器在未来安全中的重要性，而椒图在这个领域中是整个业界的翘楚。”</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"><strong><em>360企业安全集团总裁吴云坤：对抗网络黑产需要安全生态的能力协同。</em></strong></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　椒图科技总经理王健认为，只有在服务器上部署安全防护软件，让服务器自身具有防护能力，以有限行为防御无限漏洞，才是防黑的终极解决之道。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"><strong><em>椒图科技总经理王健：力求实现以有限行为防御无限漏洞。</em></strong></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　360企业安全产品经理杨中臻，现场介绍了双方联合发布的服务器自防护产品。360企业安全、椒图科技共同推出的服务器自安全防护产品，融合服务器自防护(SSP)、服务器安全加固(SSH)、服务器资产管理(SAM)、服务器安全联动(SEL)四项能力，打造万无一失的服务器安全。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"><strong><em>360企业安全产品经理杨中臻做现场讲解。</em></strong></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"><strong><em>服务器自防护产品能力图</em></strong></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　有业内人士认为，唯有依靠安全协同才能更好地对抗网络黑产，360企业安全与椒图科技的强强联合，或将进一步加强企业的安全防护能力，向用户输出更完整的安全方案，也可以让椒图科技更专注于服务器安全研发，坚守“用科技守卫信息领土”的企业初心。</p><p><br/></p>', '2', '1545032184');
INSERT INTO `yun_news` VALUES ('49', '4', '子弹短信被爆存在安全漏洞 官方回应：早已修复', '一经发布便迅速登顶App Store免费排行榜第一，上市7天下载量超过百万，上线七天完成第一轮1.5亿融资，最近子弹短信可以说是风头无两，消息多到已经分不清是正常新闻还是软文。', '../upload/news/up_5c175238ced53.jpg', 'admin', '<p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">一经发布便迅速登顶App Store免费排行榜第一，上市7天下载量超过百万，上线七天完成第一轮1.5亿融资，最近子弹短信可以说是风头无两，消息多到已经分不清是正常新闻还是软文。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　或许是物极必反，近日又有多家媒体报道称子弹短信存在严重漏洞。比如，按照网址规则输入用户的ID就可以看到用户的个人信息(所在地、绑定的<a class=\"keyword_link\" href=\"http://mydown.yesky.com/sjsoft/Android/ADLT/403/466903.shtml\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">微博</a><a class=\"keyword_link\" href=\"http://mydown.yesky.com/sjsoft/299/11799799.shtml\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">微信</a>和<a class=\"keyword_link\" href=\"http://mydown.yesky.com/soft/33/31000033.shtml\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">&nbsp;QQ</a>&nbsp;等)，而陌生人添加好友时，也可以看到对方的<a class=\"keyword_link\" href=\"http://product.yesky.com/mobilephone/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">手机</a>号。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　对此，子弹短信在29日深夜回应称，自8月20日正式上线后，由于web端接口设计疏漏，确实出现过web端显示铭文手机号和自增ID的问题。我们在21日晚上发现后立刻停止了web端服务，问题解决后，已于23日重新上线。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　针对受到网友关心的子弹短信陌生人添加好友时可以看到对方手机号的问题，子弹短信表示已在第一时间进行了紧急修复，当天就解决了这个问题。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　不过，更让用户无奈的，是《子弹短信用户协议》中的部分“任性”条款：快如科技将尽可能保障安全，但是不能完全避免安全信息泄露的风险，所以我们无法做出任何确定性的保证或承诺。由于各种原因(包括但不限于自行泄露主密码、不可抗力或第三方原因)导致用户的密码泄露带来的损失，快如科技不承担责任。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　简言之，对于信息泄露的风险，子弹短信无法做出承诺且不承担责任，这样的协议可以说是“简单粗暴”了。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　加上近日的华住信息遭泄露事件，最近关于用户隐私的问题可以说引发了不少的关注，APP为用户信息提供安全保障是义务也是责任，如若因为平台方导致信息泄露，APP理应承担责任。子弹短信的火爆离不开快如科技在创新方面所做的努力，但是，在追求产品创新性的同时，也不要忘记用户才是根本，也要注意时刻保障用户的权益不受危害。</p>', '2', '1545032248');
INSERT INTO `yun_news` VALUES ('50', '4', '东软睁开“智慧之眼” “为安全服务买单”不是乌托邦', '在10月18日东软于敦煌举办的2018东软解决方案论坛上，东软集团董事长兼CEO刘积仁博士道出了东软集团为自己做出的全新定义。', '../upload/news/up_5c17528f3b734.jpg', 'admin', '<p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">“未来的东软不仅是一家软件公司，也是用软件创造价值和新商业模式的公司，将在融合中找到新的发展空间。”在10月18日东软于<a class=\"keyword_link\" href=\"http://mydown.yesky.com/game/98/36299598.shtml\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">敦煌</a>举办的2018东软解决方案论坛上，东软集团董事长兼CEO刘积仁博士道出了东软集团为自己做出的全新定义。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"><strong>刘积仁：东软将在融合中找到自己新的发展空间。</strong></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　今天的东软，正在为<a class=\"keyword_link\" href=\"http://product.yesky.com/mobilephone/china\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">中国</a>13亿人提供人口数据服务，为7亿人提供社会保险服务与支持系统，为4亿人支付电费提供服务，为超过2.6亿户股民提供证券交易监察服务。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　在医疗领域，东软为全球110余个国家和地区的9000多家医疗机构提供数字医疗设备，为国内400多家三甲医院用户、2500多家大型医疗机构、30000多家基层医疗卫生机构提供医院信息化管理等服务。在汽车电子领域，东软正在为全球Top30汽车品牌中的80%以上的车型提供服务……</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　这组亮丽的数字验证了今日的东软已经深深融入社会各行各业，在各个角度推动行业信息化发展的同时，自身也在不断转型和创新商业模式。与此同时，当信息化应用渗透到如此广泛的领域之时，安全问题也变得更为凸显和紧迫。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　顺应需求，东软在这次大会上发布了两款最新的安全解决方案，即东软NetEye A80态势感知与协同响应平台、东软NetEye(ElvaEye)系列业务安全<a class=\"keyword_link\" href=\"http://product.yesky.com/wangguan/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">网关</a>技术方案。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"><strong>　　业务安全网关，智慧之眼明辨真假</strong></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　当“互联网+服务”在为我们查询个人社保、医保业务提供方便时，也为我们带来了新的安全风险，极易导致个人信息泄露，甚至出现大量的恶意流量，给正常的业务带来极大的风险和压力。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　在社保对公服务平台中，存在大量的第三方APP模拟正常的浏览器访问，使得信息安全险象丛生，而传统的安全设备却对此无能为力，无法识别和阻断这些行为。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"><img alt=\"\" width=\"516\" height=\"341\" src=\"/ueditor/php/upload/image/20181217/1545032301360993.jpg\"/><br/><strong>陈静相：希望NetEye能像眼睛一样护卫用户的系统。</strong></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　“东软的一家社保客户推出了官方APP，但不久他们发现，与此同时业内还出现了200多个非官方的APP，有一些APP下载量甚至达到了九百多万。” 东软集团<a class=\"keyword_link\" href=\"http://product.yesky.com/netsecurity/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">网络安全</a>事业部产品总监、车联网安全研发中心主任陈静相向记者透露，“值得注意的是，这些APP‘李鬼’们实际上在通过发布的接口伪装官方APP进行访问。”</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　在2018年9月举办的一次全国社保系统的“互联网+”会议上，人力资源与社会保障部专门就第三方APP爬取数据的问题做了特别说明，就是为了专门解决社保行业对公服务平台的个人信息泄露问题。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　为此，东软专门打造了一套完善的解决方案，“把基于SOC分析、网关流量采集的产品做了一个变化，这就是现在东软推出的NetEye(ElvaEye)系列业务安全网关技术方案。这套系统部署在客户最前端<a class=\"keyword_link\" href=\"http://product.yesky.com/hardwarefirewall/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">防火墙</a>的后面，取名的含义是智慧之眼、精准识别。”陈静相说，“可以把它视为一个小型的态势感知处置平台，希望它像眼睛一样去护卫用户的系统。之所以叫业务安全网关，实际上其与原来的网关一个独立的设备不太一样，它其实只是一个前端的采集器，策略放在里面，其实它也是个SOC。”</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　东软将“慧眼”系统部署在一家航空公司三个月后，经过数据分析，发现有96%的流量来自电子黄牛，但此前客户一直为迅速攀升的流量需求增加了很多硬件设施，这笔钱花得着实有点冤枉。“有一家社保客户曾经直接找到我们，说他们的系统所承受的访问已经快把机器拖死了。在他们部署了东软的网关产品后，发现高达200多万的访问量中只有不到10%的访问才是正常访问。”陈静相介绍说，部署了东软慧眼之后，用户发现自己其实并不需要每年都额外增加<a class=\"keyword_link\" href=\"http://product.yesky.com/cpu/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">CPU</a>和<a class=\"keyword_link\" href=\"http://product.yesky.com/memory/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">内存</a>等硬件设备的投入。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　在慧眼系统中，东软动用了四大核心技术，包括人机识别、指纹令牌、行为识别、实时决策分析等模块，系统可以对爬取数据的流量实现了快速阻断，有效防止数据泄露的风险，保障业务系统的安全，同时通过阻断大量异常流量降低后台<a class=\"keyword_link\" href=\"http://product.yesky.com/service/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">服务器</a>的负载。该套解决方案，除了部署在社保行业的对公服务平台，还可以成功部署在公积金行业的网上查询系统、航空公司的网上售票系统。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　随着越来越多的行业用户提供网上业务，东软NetEye数据防爬解决方案将为更多的用户提供安全保障。目前，部署在石家庄、秦皇岛等行业社保用户的对公服务平台系统，已经得到了用户的高度评价。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"><strong>　　借态势感知解决客户安全问题</strong></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　习近平总书记曾在今年4.19讲话中强调，要“全天候全方位感知网络安全态势”，“感知网络安全态势是最基本、最基础的工作”，“综合运用各方面掌握的数据资源，加强大数据挖掘分析，更好感知网络安全态势，做好风险防范。这项工作做好了，对国家、对社会、对企业、对民众都是有好处的。”</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　东软发布的NetEye A80态势感知与协同响应平台基于大数据技术，集中采集、存储和分析全面的网络安全要素，利用风险计算模型、人机交互分析、智能分析引擎、可视化、工作流引擎、设备协同等手段，结合领先的威胁情报，对关键信息基础设施的运行状态、面临的外部攻击、内部违规行为进行深度检测，建立快速有效的威胁检测、分析、处置能力和全网安全风险感知与协同响应能力，以达到“摸清家底，认清风险，找出漏洞，通报结果，督促整改”的目的。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"><img alt=\"\" width=\"480\" height=\"328\" src=\"/ueditor/php/upload/image/20181217/1545032301102372.jpg\"/><br/><strong>张泉：东软“A80”中的”0“代表着安全从0开始。</strong></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　“A80版本是与东软ITIL(即IT基础架构库)无缝衔接在一起，从出现问题再到处置问题，我们把它定义成能够精准地感知态势，快速地处置风险，这是A80新版的一个比较大的特点。”东软集团网络安全事业部副总经理张泉表示。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　“东软在中国安全领域的SOC落地能力是很强的，因为我们是以满足客户需求为己任的公司，迄今为止，东软所做的SOC项目没有一个烂尾，全部按时上线，几乎达成客户的全部需求，这样的响应能力在国内的SOC行业中并不多见。”陈静相向记者表示，“态势感知一定是以SOC为核心的，不管原来收集的数据有多少，云上面的处理能力有多么厉害，展示效果有多么炫酷，如果没有安全运营平台落地能力的话，就毫无效果可言。”</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　的确，解决用户的问题才是最重要的。从东软最近几个电力和银行行业客户的需求来看，他们对这方面表现出了较高的需求。面对新的安全形势，传统安全体系遭遇到前所没有的瓶颈，东软顺势发布的NetEye A80态势感知与协同响应平台，就是力求让用户享受到应用体验更友好、实现架构更开放、兼容设备更广泛、威胁情报更强大、风险防范更系统的优势，助力政府和企事业单位提升安全运营水平，加强主动防御能力。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　A80新版产品偏重于态势感知及应急处置协同响应平台，其通过海量数据分类处理，能够清晰地将前者进行展示，并将各家行业厂商多年积累下来的各种接口数据都完成<a class=\"keyword_link\" href=\"http://product.yesky.com/soundcard/in\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">内置</a>，可以兼容众多设备，同时实现协同防御。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　“东软对网络安全事业部特别重视，单独给了我们一栋楼，在整个东软集团，可以单独给一个事业部批一栋楼办公的只有网络安全事业部。”张泉笑着向记者解释了东软将新产品命名为“A80”的缘由，“那栋楼就叫A8，它是新平台的诞生地，而‘0’则代表着安全从0开始。”</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"><strong>　　未来安全并无止境</strong></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　“东软的强项就在于做平台。从我们目前所面对的客户不断增长的应用需求，以及从国际安全市场交流态势来看，安全是没有止境的。”陈静相告诉记者，“哪怕是今天刚刚建立好的一套完善的安全系统，明天它仍然可能变得不再安全。新的漏洞会不断产生，而平台的优势就在于可以为客户提供不间断的安全策略服务。”</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　东软打算尝试在东莞建立云安全运营中心，租用给一些小客户。“我们的一个<a class=\"keyword_link\" href=\"http://product.yesky.com/digitalcamera/japan\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">日本</a>合作客户就建立了一个安全云中心，安全云中心采用了SOC的一部分功能，所有的客户通过UTM采集器连接到东软安全云上，然后由这家日本公司去做整体的安全运营。未来，我相信中国有可能会出现大的安全厂商建立几个‘运营云’，之后把用户的设备、终端接进来，与‘安全云’去对接，后台有专业人士帮用户做运营。” 陈静相表示，这一模式在发达国家其实已经比较成熟了，现在国内也开始逐渐出现这种需求，这或许是东软安全平台未来的一个发展方向。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　据悉，赛门铁克今年在印度成立了全球安全运营中心，其针对的用户就是这类用户，在赛门铁克与客户签署保密协议之后，为其全球用户提供安全运营。因为通过态势感知侦测出来的安全事件需要专业人士来处置，而这种专业人才的成本是小机构和小企业承受不了的。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　“实际上，所谓的‘安全’，它绝对不是针对‘点’的防护，也不是安全产品的堆砌，同时也不只是依靠规则的设置就可以加以全面防护的。它一定是体系化的防护。很多国际厂商也都在走平台化智能化这种模式。”陈静相表示，“中国国内平台的开发还只是一个起步阶段，正在强化产品各层面之间的智能化联动和安全运营，总体而言，国内安全厂商在这方面确实考虑得还不够多，这也是东软安全未来的梦想和挑战。”</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"><strong>　　用户安全服务付费意识尚待提高</strong></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　“从我们现在试用及付费客户的沟通情况来看，现在大多数客户对服务收费的接受程度还比较低。”张泉向记者表示，很<a class=\"keyword_link\" href=\"http://product.yesky.com/email/dyh\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">多用户</a>的想法是“宁愿一次性掏50万，为我连续服务五年。”而东软的希望则是“宁愿用户先付20万，以后每年付费10万，甚至最后一年可以减免剩余的10万”。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　“这是让我们感觉挺郁闷的一件事情。”东软集团股份有限公司副总裁兼网络安全事业部总经理杨纪文向记者表示，“业务网关对客户来说确实有效、有用，很有价值，但是其存在的一个最大的问题是：它是一个攻防过程，不管是黑客也好，黄牛党也好，黑客们都在不停地摸索用户的防范规则，修改他们的攻击手段。如果没有持续的安全服务的话，用户系统顶多在三个月到六个月内有效，之后拦截率会大大下降，因此我们后台的安全攻防人员要不断地去进行策略调整，对照用户的防护效率进行规则更新和攻防对抗，这是一个持续的服务过程。”</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　杨纪文表示，“这不是用户安装好一台硬件产品就能一蹴而就的事，不是安全厂商卖掉了一台硬件设备就能解决掉用户所有的问题，所以我们衷心希望业务安全网关技术方案能与服务收费密切结合起来。”</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"><strong>杨纪文：用户迟早会步入为安全服务理智付费的时代。</strong></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　随着去年6月1号网络安全法的正式实施，网络安全已提升到了法律层面。网络安全法中做了明确规定：网络运营者对其收集的公民个人信息必须严格保密，不得泄露、篡改、毁损，网络运营者应当采取技术措施和<a class=\"keyword_link\" href=\"http://product.yesky.com/battery/qtdc\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">其他</a>必要措施，确保公民个人信息安全，防止其收集的公民个人信息泄露、毁损、丢失。在发生或者可能发生信息泄露、毁损、丢失的情况时，应当立即采取补救措施，告知可能受到影响的用户，并按照规定向有关主管部门报告。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　在杨纪文看来，目前国内的法律环境对安全服务的要求必然会越来越严苛。那么安全服务到底值不值钱？答案是显而易见的。虽然目前国内很多用户的观念尚未切换到“为安全服务买单”的理想阶段，但从市场趋势来看，早晚有一天，所有的用户必须步入为安全服务付费的时代，就像当年用户从不认可软件价值到最终认可并悉心接受一样。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-family: 微软雅黑, Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">　　对于东软而言，“为安全服务买单”不是乌托邦。</p>', '2', '1545032335');
INSERT INTO `yun_news` VALUES ('51', '4', '中国开发者现状是什么?', '提起开发者，你会想到什么？年轻、机智、冷静、直率、自我、蓝色牛仔裤等等。在一般人眼里，开发者或许等同于程序员、技术宅，它们的工作也显得“高深莫测”。', '../upload/news/up_5c17531369208.png', 'admin', '<p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">提起开发者，你会想到什么？年轻、机智、冷静、直率、自我、蓝色牛仔裤等等。在一般人眼里，开发者或许等同于程序员、技术宅，它们的工作也显得“高深莫测”。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　越来越多的新技术、新方向驱动产业、个人生活不断科技化，神秘的开发者不再稀有。在传统产业的科技化转型中，开发者群体不断壮大，这也给<a class=\"keyword_link\" href=\"http://product.yesky.com/ultrabook/jingdian\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">普通</a>人了解他们打开了一扇窗。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　众所周知，全球知名的科技互联网公司，比如<a class=\"keyword_link\" href=\"http://product.yesky.com/gamessoftware/microsoft\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">微软</a>、谷歌、Facebook每年都会召开开发者大会，不仅吸引全球各地的开发者参与，而且阐述公司的未来愿景。无疑，开发者是真正改变世界的一股力量。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　相比国外的开发者社区、群体，国内开发者却很低调。那么，国内开发者现状如何？</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　日前，《云栖社区》发布了“云栖社区2017<a class=\"keyword_link\" href=\"http://product.yesky.com/mobilephone/china\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">中国</a>开发者调查报告”，得以让大家一窥中国开发者现状。该报告从三个层面(技术特征、社会特征和个人特征)描述了中国开发者现状。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　<strong>　一.技术特征</strong>。该报告涵盖了开发环境、开发语言、编程环境、<a class=\"keyword_link\" href=\"http://product.yesky.com/database/data\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">数据库</a>、代码托管平台等11个方面。目前，<a class=\"keyword_link\" href=\"http://product.yesky.com/os/windows\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">Windows</a>以67.2%高居开发环境榜首，其次是<a class=\"keyword_link\" href=\"http://product.yesky.com/notebookpc/mac\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">MacOS</a>和<a class=\"keyword_link\" href=\"http://product.yesky.com/os/linux\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">Linux</a>。对中国开发者来说，Windows依然是受众最多的<a class=\"keyword_link\" href=\"http://product.yesky.com/os/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">操作系统</a>，无论是在平台的开放性、平台资源、开发者的熟悉程度和商业模式方面，Windows都具备非常明显的优势。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　而开发语言的使用，JavaScript位居第一，其次是<a class=\"keyword_link\" href=\"http://mydown.yesky.com/soft/442/34540942.shtml\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">Java</a>和SQL。毫无疑问，JavaScript是中国开发者最喜欢的语言。在中国开发者应用最多的前五个开发语言中，多数开发者收入稳定在1万人民币到2万人民币之间，但Java、<a class=\"keyword_link\" href=\"http://mydown.yesky.com/soft/133/33506133.shtml\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">Python</a>的高收入群体比例明显提升，这也可以部分解释为何Java受到中国开发者青睐。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　在代码托管平台方面，GitHub以30.7%排名第一，其次是公司内部工具和GitLab。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　<strong>　二.社会特征。</strong>开发者所属城市，北京、杭州和上海成为TOP3，值得注意的是杭州成为仅次于北京的开发者城市。而在西部区域，成都、西安分别成为开发者最喜欢的两大城市，但是以这两个城市为边界的更西部，则成为了开发者的“沙漠”区域。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　在所属行业方面，软件服务依然以40%高居榜首，其次是信息通讯和零售。而在企业规模上，中国大量的开发者主要供职于人员规模在1000人以下的中<a class=\"keyword_link\" href=\"http://product.yesky.com/wirelessrouter/xxqy\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">小型企业</a>当中，仅有23.5%的开发者为中国的<a class=\"keyword_link\" href=\"http://product.yesky.com/wirelessap/dxqy\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">大型企业</a>服务。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　然后是沟通工具。在中国，有66.2%的开发者选择“国民应用”<a class=\"keyword_link\" href=\"http://mydown.yesky.com/sjsoft/299/11799799.shtml\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">微信</a>作为沟通工具，44.1%的开发者选择<a class=\"keyword_link\" href=\"http://mydown.yesky.com/sjsoft/340/46828340.shtml\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">钉钉</a>作为主要的沟通工具。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　<strong>三.个人特征。</strong>在受教育状况上，有一半以上(58.6%)的开发者为大学本科教育，然后是专科和硕士。根据报告解释，多数开发者所具备的学历集中在本科及以上水平，这一比例高达71.9%。显然，中国开发者的平均受教育程度高于全球的平均水平。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　另一方面，“高薪”也是中国开发者的重要特征。87.4%的中国开发者月收入水平高于5000，67.6%的开发者月收入达到<a class=\"keyword_link\" href=\"http://product.yesky.com/chukongyitiji/8000up\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">8000元以上</a>。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　不过，“女性”开发者在中国依然是稀有物种。根据本次的报告，仅有7.9%的女性从事开发者工作，这一比例与2017年Stack Overflow全球开发者调研中的结果接近。而在开发技术领域方面，Web开发、前端开发、云计算、大数据、人工智能、移动开发、区块链和安全成为最突出的八大特征。</p>', '2', '1545032467');
INSERT INTO `yun_news` VALUES ('52', '4', 'Unite Beijing 2018 今年为游戏开发者带来了什么？', '5月11日，Unite Beijing 2018大会在北京国家会议中心拉开帷幕。', '../upload/news/up_5c17539ea9993.jpg', 'admin', '<p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">5月11日，Unite Beijing 2018大会在北京国家会议中心拉开帷幕。作为游戏开发者的盛宴，今年的Unite大会Keynote汇聚了12位重量级嘉宾，不仅展示了Unity取得的成绩，而且为开发者带来了许多新工具。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　Unity作为全球知名的游戏开发平台，其CMO Downie表示，创造者的存在让世界变得更加美好。Unity研发团队秉承着开发大众化，解决疑难问题与助力开发者获得成功的三大宗旨，不断通过引擎技术和功能的更新迭代，让开发者的创意得到充分发挥。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　目前，在移动端游戏领域，全球50%的移动游戏适用Unity，利用Unity开发制作的游戏运行于全球60亿设备上，游戏的下载量高达240亿。同时，在<a class=\"keyword_link\" href=\"http://product.yesky.com/tdsxssb/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">VR</a>内容方面，69%的<a class=\"keyword_link\" href=\"http://product.yesky.com/product/980/980057/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">Oculus Rift</a>平台VR内容、74%的<a class=\"keyword_link\" href=\"http://product.yesky.com/product/980/980069/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">HTC Vive</a>平台VR内容和87%的Gear VR平台内容及91%的HoloLens平台内容均使用Unity进行开发。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　现在，全球超过25家知名平台与Unity达成合作，Unity开发者可以轻松地将游戏与应用部署到各大平台。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　这些数字不仅说明了Unity耀眼的成绩，而且也昭示着其在游戏开发领域中的领导者地位。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　当然，本次大会不仅仅是来展示成绩的，更多的内容是为开发者带来新工具。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　<strong>　Unity 2018全面升级 新工具助力游戏开发者</strong></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　Unity全球技术专家总监Carl Callewaert展示了一系列实用且令人惊喜的新功能，比如全新的美术工具、下一代渲染管线、Real-time Ray Tracing GPU Lightmapper和Nested Prefabs等。而Unity全球内容开发专家Andy Touch通过比较不同时期的Unity Demo内容，展现了从2015年至今的Unity功能亮点，并基于Unity最新的实时渲染影视短片《死者之书》为大家介绍了高清渲染管线(HD RP)的功能。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　Unity全球技术美术总监江毅冰则为大家展示Unity实时渲染动画电影《发条乐师》(Windup)的惊艳画面和创作细节。《发条乐师》是在艺术家和编写高清渲染流程的程序员紧密合作下完成的，并充分发挥了Unity实时渲染的优势，让传统影视动画制作流程中难以实现的多人协作成为可能。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　<strong>　轻量级Unity出世——更小、更快、更高效</strong></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　本次大会，Unity全球技术专家Mark Schoennagel分享了备受开发者期待的轻量级Unity。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　在开发轻量级应用时，开发者因<a class=\"keyword_link\" href=\"http://product.yesky.com/scaner/wenjian\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">文件</a>过大，而导致下载和启动的速度过慢，进而影响性能表现。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　针对上述问题，Unity创建了全新的模块架构，使得Unity应用能流畅地运行在轻量级设备和网页上。目前，基于Web的应用，新的Unity核心运行时的文件只有72KB大小。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　同时，Unity也对资产管线进行了优化，进一步缩小文件大小，让开发者可以更高效地完成轻量级项目优化。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　<strong>　引入机器学习 游戏开发步入智能时代</strong></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　今年的Unity大会，机器学习被引入游戏开发领域，成为重磅之一。通过集成机器学习，Unity让游戏开发流程变得更加简单。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　Unity AI与机器学习副总裁Danny Lange介绍了Unity ML-Agents。它是一款开源的AI工具包，能帮助开发者和研究人员在真实且复杂的环境中训练机器代理。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　Danny着重介绍了全新发布的ML-Agents中的新增特性 ——模拟学习，通过具体案例展示游戏角色如何根据真实的人类玩家行为进行实时模拟。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　除了游戏开发，机器学习技术还将应用到整个Unity的生态系统中，帮助开发者更加智能地完成各种优化。同时，Danny还介绍了Unity用于性能设置优化的服务LiveTune, 以及全新的推广服务IAP Promo，以更加智能化的服务帮助开发者实现精准的优化与推广。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　此外，Unity Ads大中华区客户管理总监胡敏在Keynote上宣布了针对<a class=\"keyword_link\" href=\"http://product.yesky.com/mobilephone/china\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">中国</a><a class=\"keyword_link\" href=\"http://mydown.yesky.com/sjsoft/0/34842000.shtml\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">安卓市场</a>的Unity Ads官方直链广告解决方案。这一部署便捷，安全性极高的变现解决方案，无疑是为中国开发者量身定制的创收利器。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　而Unity全球教育总监Anuja Dharkar则为大家介绍了Unity全球教育认证体系，开发者可通过多种官方渠道，如线上交互式教程与线下官方培训等，全面掌握Unity技能并获得官方认证。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　最后，Unity大中华区总经理兼全球副总裁张俊波则全面介绍了Unity中国全面的业务体系。更重要的是，未来还将有更多本地化的Unity服务和业务团队落地中国，助力中国开发者获得成功。</p>', '4', '1545032606');
INSERT INTO `yun_news` VALUES ('53', '4', '微服务——程序员的自我修养', '微服务在2016年成为仅次于物联网和认知计算的第三热门技术，微服务究竟有何等神通，让ThoughtWorks、Netflix、Amazon、Uber等公司趋之若鹜？', '../upload/news/up_5c17541b39110.jpg', 'admin', '<p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">微服务在2016年成为仅次于物联网和认知计算的第三热门技术，微服务究竟有何等神通，让ThoughtWorks、Netflix、Amazon、Uber等公司趋之若鹜？</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　<strong>　传统应用之殇</strong></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　版本发布·千年等一回 在过去传统企业应用开发模式下，每当遇到需求要修改时，不得不按照大颗粒系统发布版本，瀑布式的版本计划困住了程序员那优雅的双手。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　应用升级·祝你平安 应用无法做到永远在线，大版本升级时，不得不停机中断服务。一个小小的问题就有可能带来整个系统的崩溃。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　技术选型·独角戏 技术实现单一，需要想办法用一种技术解决所有问题。每个问题都变成钉子，每个方案都被当成锤子。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　<strong>微服务·初见</strong></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　如何解决传统应用开发模式逐渐暴露出来的牢骚？随着领域驱动设计、持续交付、按需虚拟化、基础设施自动化、小型自治团队等实践的流行，微服务架构逐渐地被广大勤劳智慧的架构师们归纳总结而出：</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　微服务架构风格，描述的是一个大型复杂软件应用系统由若干个微服务组成。系统中的各个微服务能被独立部署和扩展，每个微服务还能提供一个稳固的模块边界。各个微服务之间是松耦合的，微服务很小，专注于做好一件事情。微服务框架带了良好的技术异构性、弹性、扩展性，它的简化部署为持续交付提供了巨大推动力。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　<strong>微服务·爱你在心口难开</strong></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　事实是大量传统应用在微服务改造过程并没有想象的那么顺滑，虽然微服务带来的好处多多，同时也引入了很多新的挑战，如：</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　<strong>分布式编程难度大、有风险</strong>：分布式编程难度更大，远程调用更慢且总存在失败的风险</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　<strong>需处理分布式系统的一致性</strong>：对分布式系统来说，保持一致性非常困难，意味大家都要处理最终一致性</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　<strong>增加运维复杂性</strong>：需要一个成熟的团队(机制)来管理大量需要频繁部署的服务</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　那么，是否该用微服务架构？什么时候用微服务架构？该用什么样的微服务架构？怎么用微服务架构？遗留业务如何快速迁移上云？微服务化之后繁杂的微服务如何管理运维？</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\"><img alt=\"\" src=\"/ueditor/php/upload/image/20181217/1545032653101038.jpg\" width=\"421\" height=\"221\"/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　<strong>微服务·多了“引擎”两个字</strong></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　如何解决以上问题？</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　你也许希望有一个开发框架，能够帮助各企业和开发者最大化的降低应用微服务化的门槛。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　你也许希望有一些工具，能够有力支撑企业在微服务架构下的高效开发。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　你也许希望有一个平台，能够帮助企业和开发者全方位管理改造后的大量微服务。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　你也许希望有一位领先微服务改造专家，能够给予专业的微服务化解决方案指导。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　<strong>微服务引擎CSE是全新的<a class=\"keyword_link\" href=\"http://product.yesky.com/ssd/enterprise\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">企业级</a>微服务开发与管理平台，包含了微服务SDK、一系列配套的开发工具以及一站式微服务应用管理平台，致力于为企业提供0门槛服务化改造，为企业提供微服务全生命周期管理服务。在CSE上线之前先让我们露出一小部分核心性感的特性刷新你的微服务观。</strong></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　<strong>　1.开放是一种人生境界</strong></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　CSE支持多开发模式多语言多协议，传统应用改造0门槛。无论遗留应用是SpringMVC、透明代理、亦或是JAX-RS的开发模式，业务代码0改动就可以完成微服务改造。无论你是倾心GO语言还是青睐<a class=\"keyword_link\" href=\"http://mydown.yesky.com/soft/442/34540942.shtml\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">JAVA</a>，无论你是执着REST协议还是钟情RPC，没有做不到，只有你想不到。同时，CSE-SDK还兼容spring-cloud主流社区，保持与业界生态能力互通。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　<strong>2.用一杯咖啡的时间完成微服务改造</strong></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　CSE黑科技助力传统应用0修改服务化。当然开发者可能已经写好了一些微服务或者真的一丁点儿都不想改动遗留代码。CSE提供了一项黑科技，支持非侵入接入微服务治理。给你一个微服务摇篮，让遗留应用无缝接入CSE微服务治理与管控平台，让你的应用安全可靠<a class=\"keyword_link\" href=\"http://product.yesky.com/lcd/sxseries/sumsung/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">舒心</a>。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　<strong>3.周末再也不想接到加班<a class=\"keyword_link\" href=\"http://product.yesky.com/telephone/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">电话</a>？</strong></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　一站式微服务应用管理云服务，服务论剑随时随地：在微服务开发完毕之后，繁杂的微服务实例如何管理、如何监控、如何运维？微服务引擎的一站式微服务应用管理平台，提供微服务全生命周期管理，提供微服务治理、微服务安全管理、灰度发布、分布式事务等等特性。让你享受谈笑间樯橹灰飞烟灭的商业快感!</p>', '21', '1545032731');
INSERT INTO `yun_news` VALUES ('54', '4', '一周AI大事盘点：阿里语音技术超谷歌，AI或用于识别股票违规交易', '今天就带你盘点过去一周(12月10日至12月16日)AI领域发生的热点门事件：', '', 'admin', '<p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">过去的一周中AI领域都发生了哪些重大事件？AI应用范围又新增了哪些领域？行业大咖又发表了哪些不同的观点？今天就带你盘点过去一周(12月10日至12月16日)AI领域发生的热点门事件：<br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\"><strong>　　<a class=\"keyword_link\" href=\"http://product.yesky.com/digitalcamera/japan\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">日本</a>引进AI监管系统，识别股票交易违规行为</strong></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　12月10日消息，为了识别股市的违规和虚假财报，日本证券交易监视委员会正考虑引进AI市场监督系统。日本证监会希望通过这种方式来取代人类员工，能够在违规行为发生时就立即做出反应。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"http://127.0.0.1/yiyun/upload/ueditor_img/20181218/1545120421767213.png\" title=\"1545120421767213.png\"/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　速评：目前使用<a class=\"keyword_link\" href=\"http://product.yesky.com/pc/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">计算机</a>进行股票交易越来越普及，随着交易量及交易金额的不断增长，金融监管开始变得越来越困难。因此利，利用AI监管系统进行交易监管是很有必要的。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　<strong>　<a class=\"keyword_link\" href=\"http://mydown.yesky.com/sjsoft/272/35340272.shtml\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">百度</a>李彦宏入围“全球最受关注十大AI领军人物”</strong></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　12月10日消息，《哈佛商业评论》评选出了“全球最受关注的十大AI领军人物”，百度创始人、董事长兼首席执行官李彦宏上榜，位列第三，成为<a class=\"keyword_link\" href=\"http://product.yesky.com/mobilephone/china\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">中国</a>唯一上榜企业家。《哈佛商业评论》对李彦宏的评价是“中国AI行业的启蒙者与设计师”，是他首度提出了AI<a class=\"keyword_link\" href=\"http://product.yesky.com/product/470/470139/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">三维</a>一体的标准，认为未来企业都需AI化。李彦宏在全球范围内最早搭建无人驾驶开放平台，让汽车全行业进入AI，大大推动了中国及世界智能驾驶行业的发展。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"http://127.0.0.1/yiyun/upload/ueditor_img/20181218/1545120421581445.png\" title=\"1545120421581445.png\"/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　速评：目前，在专利申请方面，中国已成为全球人工智能领域专利最多的国家。百度作为国内人工智能专利申请量排名第一的企业，经过几年的发展，在人工智能领域已取得了多项突破性技术成果，相信未来百度将创造出更多技术与应用相结合的场景。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\"><strong>　　清华大学发布《人工智能芯片技术白皮书(2018)》</strong></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　12月11日消息，在第三届未来芯片论坛上，清华大学联合北京未来芯片技术高精尖创新中心发布《人工智能芯片技术白皮书(2018)》。《白皮书》提出AI芯片基准<a class=\"keyword_link\" href=\"http://product.yesky.com/ceshi/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">测试</a>和发展路线图，完成对AI芯片各种技术路线的梳理，并对未来技术发展<a class=\"keyword_link\" href=\"http://product.yesky.com/feedsystem/trendnet/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">趋势</a>和风险进行了预判。据悉，《白皮书》由斯坦福大学、清华大学、香港科技大学、加州大学、圣母大学的顶尖研究者和产业界资深专家共同编写完成。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"http://127.0.0.1/yiyun/upload/ueditor_img/20181218/1545120421137959.png\" title=\"1545120421137959.png\"/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　速评：作为人工智能应用实现的关键支撑，AI芯片已成为人工智能领域的研究热点。在此之前，关于AI芯片，缺少一部从技术标准、技术发展趋势等方面的权威性专著，而《白皮书》的发布及时填补了这个空白。可以说，这将对AI芯片技术未来发展<a class=\"keyword_link\" href=\"http://product.yesky.com/chargesystem/direction/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">方向</a>具有重要的启示意义。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\"><strong>　　《麻省理工科技评论》：阿里AI语音技术<a class=\"keyword_link\" href=\"http://product.yesky.com/pc/chaoyue/tongfang/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">超越</a>谷歌</strong></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　12月11日消息，阿里巴巴达摩院的科学家在神经信息处理系统大会上，演示了其AI语音技术。在大约半分钟的时间里，菜鸟语音助手在经历了被用户打断、更改意图、沉默等交互后，仍然能够应对自如，顺利帮助用户修改了派送地址。《麻省理工科技评论》称，阿里巴巴的AI语音助手可以完成复杂的人类对话功能，甚至能够读懂人类的潜在意图，这表明阿里巴巴AI语音技术已经超越了谷歌。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img alt=\"\" src=\"/ueditor/php/upload/image/20181218/1545120168136479.png\" width=\"551\" height=\"331\"/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　速评：语音识别是人工智能领域极为重要的前沿技术，在自动驾驶、<a class=\"keyword_link\" href=\"http://product.yesky.com/smarthome/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">智能家居</a>等领域都有广泛应用。菜鸟语音助手不仅能够免去快递员的沟通成本和时间，还将为人们带来更好的物流体验。可以说，阿里在语音识别领域取得的突破，将会为以后语音技术的发展奠定坚实基础。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\"><strong>　　<a class=\"keyword_link\" href=\"http://product.yesky.com/mobilephone/usa\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">美国</a>斯坦福大学发布2018年《人工智能指数》报告</strong></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　12月13日消息，美国斯坦福大学发布的2018年《人工智能指数》报告显示，2007年到2017年间，欧洲作者发表的人工智能论文最多，占总数的28%，中国以25%位列第二，中国在此期间发表的论文数量增加了150%。报告指出，中国的人工智能论文主要集中在农业科学领域以及工程技术领域，欧美论文更侧重于人文学科和医疗健康领域。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"http://127.0.0.1/yiyun/upload/ueditor_img/20181218/1545120421941363.jpg\" title=\"1545120421941363.jpg\"/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　速评：当前提到科技的创新，人工智能成为绕不过的一关。事实上，世界各国在人工智能基础领域之争才刚刚开始，论文、专利的竞争还要持续一段时间，而更重要的一点是，当技术研究取得突破之后，如何将基础研究转化为国家以及企业的竞争力，将是未来观察各国人工智能发展趋势的重要切入点。</p><p><br/></p>', '2', '1545120187');
INSERT INTO `yun_news` VALUES ('55', '4', '专利之争还是5G之争？高通苹果大战蔓延到美国本土', '前言：高通公司与苹果公司之间的专利战持续已久，近日，双方矛盾再次升级。', '../upload/news/up_5c18abbb4210a.png', 'admin', '<p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">前言：高通公司与<a class=\"keyword_link\" href=\"http://product.yesky.com/wirelessrouter/apple/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">苹果</a>公司之间的专利战持续已久，近日，双方矛盾再次升级。继福州市中级人民法院裁定，要求苹果公司停止针对高通专利的侵权行为，并禁止苹果在<a class=\"keyword_link\" href=\"http://product.yesky.com/mobilephone/china\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">中国</a>市场进口和销售涉嫌侵犯专利的部分型号的<a class=\"keyword_link\" href=\"http://product.yesky.com/mobilephone/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">手机</a>之后，高通公司又向<a class=\"keyword_link\" href=\"http://product.yesky.com/mobilephone/usa\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">美国</a>国际贸易委员会(ITC)申请了<a class=\"keyword_link\" href=\"http://product.yesky.com/mobilephone/apple/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">iPhone手机</a>进口禁令。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"http://127.0.0.1/yiyun/upload/ueditor_img/20181218/1545120625611696.png\" title=\"1545120625611696.png\"/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　<span style=\"margin: 0px; padding: 0px; border: 0px; font-weight: bold;\">I<a class=\"keyword_link\" href=\"http://product.yesky.com/structuredcablingsystem/tc/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">TC</a>考虑对<a class=\"keyword_link\" href=\"http://product.yesky.com/mobilephone/apple/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">iPhone</a>下达进口禁令？</span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　据彭博社报道，高通试图说服美国国际贸易委员会考虑对使用<a class=\"keyword_link\" href=\"http://product.yesky.com/tabletpc/x86\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">英特尔芯</a>片的苹果iPhone下达进口禁令。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　而就在几天之前，福州市中级人民法院作出裁定，因苹果侵犯高通两项专利，要求苹果立即停止在中国进口、销售和许诺销售未经授权的产品的侵权行为。相关产品包括：iPhone 6S、iPhone 6S Plus、iPhone 7、<a class=\"keyword_link\" href=\"http://product.yesky.com/product/967/967565/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">iPhone 7 Plus</a>、iPhone 8、iPhone 8 Plus和iPhone X。现在，高通又试图给苹果在美国的进口发出致命一击，让苹果在中美这两个全球最大的消费市场都面临困境。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　美国国际贸易委员会在12月12日表示，将仔细斟酌一位美国法官提出的，关于iPhone进口禁令或将损害美国在5G技术领域竞争优势的观点，并且将研究美国侵犯高通<a class=\"keyword_link\" href=\"http://product.yesky.com/battery/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">电池</a>节能技术的调查结果。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　需要指出的是，美国国际贸易委员会是为了保护美国市场，免受外国产品的倾销或补贴等不公平贸易行为的影响。如果，美国国际贸易委员会发现进口商侵犯了美国专利，就会发布进口禁令。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　在2017年7月，高通公司就向美国国际贸易委员会发起诉讼，称采用英特尔芯片的iPhone侵犯了该公司的6项专利。2018年9月，美国国际贸易委员会裁定，苹果只侵犯了其中的1项高通专利(<a class=\"keyword_link\" href=\"http://product.yesky.com/power/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">电源</a>管理技术)，驳回高通要求就此颁布禁售令，阻止采用英特尔芯片的iPhone进口到美国市场的请求。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　然而，在12日事件却发生了惊人逆转，高通终于说服美国国际贸易委员会对9月的裁决进行复审，而最终裁决将于2月19日前做出。如果美国国际贸易委员会做出不利于苹果的裁定，这将意味着，苹果在中美两国都遇到了麻烦，而且可能是致命性的。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　<span style=\"margin: 0px; padding: 0px; border: 0px; font-weight: bold;\">高通苹果5G之争，引众多组织站队</span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　法官托马斯·彭德在9月的调查结果中表示：“国家安全问题是一个非常重要的问题，当5G的发展、创新、控制和主导地位将对美国经济的竞争条件产生如此巨大的影响时。如果委员会按照高通的要求发布禁令，会在非常广泛的基础上对美国造成真正的损害。”</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　此外，该案件已经引起了许多团体和组织的关注，保守派团体表示，苹果如果赢了，代表着对知识产权的不尊重，而软件应用开发商的一名代表则表示，如果高通赢了，就会损害市场竞争。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　包括爱立信、<a class=\"keyword_link\" href=\"http://product.yesky.com/wirelessnetworkcard/nokia/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">诺基亚</a>和法国政府支持的France Brevet在内的IP Europe组织认为，高通对<a class=\"keyword_link\" href=\"http://product.yesky.com/mouse/4g\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">4G</a>和5G标准做出了重大技术贡献，但英特尔和苹果共同做出的贡献相对较少。而<a class=\"keyword_link\" href=\"http://product.yesky.com/pc/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">计算机</a>与通信行业协会则表示，该案代表着高通意图恢复对基带芯片组市场的实际垄断。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img alt=\"\" width=\"570\" height=\"360\" src=\"/yiyun/upload/ueditor_img/20181218/1545120582254791.png\"/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　<span style=\"margin: 0px; padding: 0px; border: 0px; font-weight: bold;\">　高通与苹果之间，是专利之争亦是5G之争</span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　高通与苹果之间的纷争由来已久，此前高通为苹果提供芯片及专利技术，苹果为高通带来相应的专利授权许可收入。不过，近两年来，高通与苹果之间的矛盾不断激化，两家科技巨头围绕专利授权费展开了多个回合的诉讼。此后，英特尔也被卷了进来，因为诉讼关系，苹果在发布iPhone XS系列时，干脆弃用了高通芯片，选择了英特尔的芯片。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　极高的移动数据传输效率、大幅提升的数据容量，谁最先开发出来，谁就能获取丰厚的利润。5G让网络服务商以及设备制造商趋之若鹜，这对于芯片供应商高通来说也是如此。继续保持全球手机芯片霸主地位，对高通来说是十分重要的机会。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　在2017年，高通发布了全球首款5G全频<a class=\"keyword_link\" href=\"http://product.yesky.com/modem/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">调制解调器</a>，2018年英特尔也推出了它的第一款5G调制<a class=\"keyword_link\" href=\"http://product.yesky.com/modem/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">解调器</a>。在此之前，据FastCompany的一份报告称，苹果推出的首款5G手机可能会使用英特尔8161 5G调制解调器芯片，并且将在2020年上半年上市。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　12月4日，在夏威夷召开的骁龙技术峰会上，高通宣布推出<a class=\"keyword_link\" href=\"http://product.yesky.com/ultrabook/shangyong\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">商用</a>5G移动平台：Qualcomm骁龙855移动平台。这意味着2019年将会有商用的5G<a class=\"keyword_link\" href=\"http://product.yesky.com/mobilephone/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">智能手机</a>推出。</p><p style=\"text-align: center;\"><img src=\"/yiyun/upload/ueditor_img/20181218/1545120625436342.png\" title=\"1545120625436342.png\"/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　现在来看，高通苹果的专利之争已演变成为高通与“苹果+英特尔”两个阵营5G之争了。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　<span style=\"margin: 0px; padding: 0px; border: 0px; font-weight: bold;\">写在最后：</span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　此前，美国对<a class=\"keyword_link\" href=\"http://product.yesky.com/wirelessrouter/huawei/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">华为</a>、<a class=\"keyword_link\" href=\"http://product.yesky.com/switchboard/zte/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">中兴</a>采取各项禁售禁令，并且还怂恿其部分盟国禁用华为5G设备。但是，在处理本国5G的问题上，美国国际贸易委员会却为难了起来。在打压了华为和中兴之后，或许美国的”内讧“将要到来了。</p><p><br/></p>', '1', '1545120699');
INSERT INTO `yun_news` VALUES ('56', '4', '边缘计算对人工智能发展有什么影响？', '前言：随着人工智能技术的不断成熟，人工智能已经应用到多个领域。', '../upload/news/up_5c18ae3f44432.png', 'admin', '<p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">前言：随着人工智能技术的不断成熟，人工智能已经应用到多个领域。现如今，人工智能技术开始在边缘侧扩展，庞大的数据量需要快速有效地分析，这极大增强了对于边缘计算的需求，边缘计算的重要性因而逐渐凸显。近日，清华大学发布的《人工智能芯片技术白皮书(2018)》提到，随着人工智能应用生态的爆发，越来越多的人工智能应用开始在端设备上开发和部署。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"http://127.0.0.1/yiyun/upload/ueditor_img/20181218/1545120820555109.png\" title=\"1545120820555109.png\"/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\">清华大学联合北京未来芯片技术高精尖创新中心发布《人工智能芯片技术白皮书(2018)》</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　<strong>　清华发布《人工智能芯片技术白皮书(2018)》，边缘计算正在崛起!</strong></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　目前，人工智能的计算大多发生在数据中心，但是随着技术的发展，与硬件紧密结合的<a class=\"keyword_link\" href=\"http://product.yesky.com/rqz/qrs\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">嵌入式</a>人工智能正受到越来越多的重视。物联网拥有海量的终端设备，如果这些设备产生的数据都需要上传云端进行智能处理或者深度学习，将会对网络带宽带来相当大的挑战。而边缘计算的诞生，就解决了这个问题。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　近日，北京未来芯片技术高精尖创新中心和清华大学微电子学研究所联合主办的“第三届未来芯片论坛上，清华大学正式发布了《人工智能芯片技术白皮书(2018)》(简称《白皮书》)。《白皮书》中提到，随着人工智能应用生态的爆发，越来越多的人工智能应用开始在端设备上开发和部署。<a class=\"keyword_link\" href=\"http://product.yesky.com/mobilephone/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">智能手机</a>是目前应用最广泛的边缘计算设备，<a class=\"keyword_link\" href=\"http://product.yesky.com/mobilephone/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">手机</a>行业巨头例如<a class=\"keyword_link\" href=\"http://product.yesky.com/wirelessrouter/apple/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">苹果</a>、<a class=\"keyword_link\" href=\"http://product.yesky.com/wirelessrouter/huawei/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">华为</a>、高通在内的手机芯片厂商相继推出，或正在研发专门适应人工智能应用的芯片产品。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　伴随着人工智能的发展，智能终端产品不断涌现，让人们真切感受到了智能改变生活。人工智能作为一种<a class=\"keyword_link\" href=\"http://product.yesky.com/ssd/usual\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">通用</a>型技术，在自身发展的同时也推动着<a class=\"keyword_link\" href=\"http://product.yesky.com/battery/qtdc\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">其他</a>技术的发展，而其他相关技术的发展反过来又加速了人工智能的进步。而即将落地的5G就为人工智能数据的传送速度及质量提供了有效保证。尽管，人工智能现在已经取得了相当大的突破，但仍然面临着许多挑战。特别是在物联网时代，随着智能终端产品的增长，大量数据通过有限的网络连接传输至数据中心进行运算后，再传回到设备端，这将导致额外的延时并浪费宝贵的带宽。而现阶段，就需要边缘技术解决。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\"><strong>　　什么是边缘计算？</strong></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　边缘计算是指在靠近物或数据源头的一侧，采用网络、计算、存储、应用核心能力为一体的开放平台，就近提供最近端服务。这样能够减少请求响应时间、减少网络带宽同时，保证数据的安全性。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"http://127.0.0.1/yiyun/upload/ueditor_img/20181218/1545120820181723.png\" title=\"1545120820181723.png\"/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\"><strong>　</strong>　那么，边缘计算具有什么特点呢？</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　1、低延时：因为边缘计算靠近数据接收源头，所以能够实时获取数据并对数据进行分析处理。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　2、高效率：边缘计算是相对于云计算更靠近设备端，可以在边缘节点处实现对数据的分析和处理，不需要等待数据传输的时间，所以效率会更高。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　3、更安全：边缘计算在获取数据之后，可以对数据加密之后再进行传输，大大提升了数据的安全性。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　4、缓解流量压力：边缘计算在进行云端传输时通过边缘节点进行一部分简单数据处理，当面对大量数据时，可以通过压缩算法，提取到有用信息之后再进行传输，这样可以降低带宽资源消耗。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　<strong>边缘计算实现智能互联世界!</strong></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　虽然边缘计算有许多不同的使用场景，但是它的本质与人工智能、物联网密切相关。目前，人工智能应用更多的是依靠云端，边缘计算则是将智能从云端转向边缘。未来，如果没有边缘计算的支持，将会有很多应用可能都无法实现。例如：自动驾驶、远程医疗以及智慧城市等。这些智能终端产品或解决方案，都是不允许超过数毫秒的时延，并对于抖动或时延变化极其敏感的。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　在很多场景下，尤其是使用封闭式自动化操作来维护高可用性的场景，响应时间必须保证在几十毫秒内，而这种条件如果没有边缘计算是无法达成的。例如：目前比较火热的自动驾驶汽车对实时信息交互和数据传输、交互的延迟指标要求非常严格，万一系统响应慢，轻则自动驾驶的体验度将大幅降低，重则会发生交通事故。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　因此，边缘计算的重要性逐渐凸显，在终端设备对部分数据进行分析处理，将有效解决网络拥堵的问题，同时能提高系统运行效率。随着边缘计算的进步，本地设备的运算能力将不断增强，使得人工智能算法能够在离开云计算的情况下正常运行。</p><p style=\"text-align: center;\"><img src=\"/yiyun/upload/ueditor_img/20181218/1545120820711444.png\" title=\"1545120820711444.png\"/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\"><strong>　　写在最后：</strong></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　如今，人工智能已经成为社会发展的必然<a class=\"keyword_link\" href=\"http://product.yesky.com/feedsystem/trendnet/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">趋势</a>，而人工智能能够正式进入到智能终端设备的原因，主要是依靠边缘计算。 作为未来社会、经济运行的基础设施的重要组成部分，边缘计算将像助推器一样在其中发挥着巨大的作用。只有边缘计算的成熟、普及，物联网、智慧城市等才有机会加速。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　相信在未来，我们将看到越来越多的边缘计算的应用场景。到那时，边缘计算将会打造一个更智能的互联世界，边缘计算技术值得期待!</p><p><br/></p>', '1', '1545120846');
INSERT INTO `yun_news` VALUES ('57', '4', '机框式核心交换机硬件架构演进', '本文将通过对这三种硬件架构、报文转发流程等原理的分析，全面剖析三种架构的优劣势。', '../upload/news/up_5c18ae27e0832.png', 'admin', '<p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">随着互联网业务的高速发展，对构建互联网基础架构的网络设备提出了更高要求，例如容量、性能、扩展性以及QoS等诸多关键特性，而这往往是由其所采用的硬件架构决定的。<strong>以框式核心<a class=\"keyword_link\" href=\"http://product.yesky.com/switchboard/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">交换机</a>为例，先后出现了多种硬件架构，而现在最为常用的有三种：Full-Mesh交换架构、Crossbar矩阵交换架构和基于Cell的CLOS交换架构。本文将通过对这三种硬件架构、报文转发流程等原理的分析，全面剖析三种架构的优劣势。</strong></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　<strong>　名词解释</strong></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"http://127.0.0.1/yiyun/upload/ueditor_img/20181218/1545121178422658.png\" title=\"1545121178422658.png\"/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　<strong>Full-Mesh</strong></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\"><strong>　　架构说明</strong></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img alt=\"\" width=\"191\" height=\"166\" src=\"/yiyun/upload/ueditor_img/20181218/1545121067910337.png\"/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\">图1：Full-Mesh架构图</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　如图1所示，所有业务线卡通过背板走线连接到其它线卡，因为Full-Mesh不需要外部的交换芯片，而是任意两个节点间都有直接连接，故得名全连接。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　由于各线卡需要Full-Mesh互联，一个节点数为N的Full-Mesh，连接总数为【N×(N-1)】÷2，所以随着节点数量增加连接总数也急剧上升，因而可扩展性较差，仅适用于槽位数量较少的核心设备。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\"><strong>　　报文转发流程</strong></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　1. 报文从线卡进入，跨卡报文送到与目的线卡连接的背板通路;</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　2. 报文到达目的线卡。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　<strong>　Crossbar</strong></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\"><strong>　　架构说明</strong></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"http://127.0.0.1/yiyun/upload/ueditor_img/20181218/1545121178306300.png\" title=\"1545121178306300.png\"/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\">图2:Crossbar架构图</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　如图2所示，业务线卡通过背板走线连接到Crossbar芯片上，Crossbar芯片集成在主控引擎上。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"http://127.0.0.1/yiyun/upload/ueditor_img/20181218/1545121178116753.png\" title=\"1545121178116753.png\"/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\">图3:Crossbar芯片架构</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　Crossbar芯片架构如图3所示，每一条输入链路和输出链路都有一个CrossPoint，在CrossPoint处有一个<a class=\"keyword_link\" href=\"http://product.yesky.com/radio/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">半导体</a><a class=\"keyword_link\" href=\"http://product.yesky.com/epu/power\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">开关</a>连接输入线路和输出线路，当来自某个端口的输入线路需要交换到另一个端口的输出点时，在<a class=\"keyword_link\" href=\"http://product.yesky.com/cpu/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">CPU</a>或交换矩阵的控制下，将交叉点的开关连接，数据就被发到另一个接口。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　简单地说，Crossbar 架构是一种两级架构，它是一个开关矩阵，每一个CrossPoint都是一个开关，交换机通过控制开关来完成输入到特定输出的转发。如果交换具有N个输入和N个输出，那么该Crossbar<a class=\"keyword_link\" href=\"http://product.yesky.com/switchboard/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">&nbsp;Switch</a>就是一个带有N*(N-1)≈N²个CrossPoint点的矩阵，可见，随着端口数量的增加，交叉点开关的数量呈几何级数增长。对于Crossbar芯片的电路集成水平、矩阵控制开关的制造难度、制造成本都会呈几何级数增长。所以，采用一块Crossbar交换背板的交换机，所能连接的端口数量也是有限的。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\"><strong>　　报文转发流程</strong></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\"><strong>　　无缓存Crossbar</strong></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　每个交叉点没有缓存，业务调度采用集中调度的方式，对输入输出进行统一调度，报文转发流程如下：</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　1. 报文从线卡进入，线卡先向Arbiter请求发送;</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　2. Arbiter根据输出端口队列拥塞情况，决定是否允许线卡发送报文到输出端口;</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　3. 报文通过Crossbar转发到目的线卡输出端口。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　由于是集中调度，所以仲裁器的调度算法复杂度很高，扩展性较差，系统容量大时仲裁器容易形成瓶颈，难以做到精确调度。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\"><strong>　　缓存式Crossbar</strong></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　最早的缓存式Crossbar只有交叉节点带缓存，而输入端是无缓存的，被称为”bus matrix”,后来，CICQ的概念被引入，即在输入端用大的Input Buffer，在中间节点用小的CrossPoint Buffer。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　这种结构采用分布式调度的方式进行业务调度，即输入和输出端都有各自的调度器，报文转发流程如下：</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　1. 报文从线卡进入，输入端口通过特定的调度算法(如RR算法)独立地选择有效的VOQ;</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　2. 将VOQ队列头部分组发送到相应的交叉点缓存;</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　3. 输出端口通过特定的算法在非空的交叉点缓存中选择进行服务。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　由于输入和输出的调度策略相互独立，所以很难保证交换系统在每个时隙整体上达到最佳匹配状态，并且调度算法复杂度和交换系统规模有关，限制了其扩展性。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\"><strong>　　CLOS</strong></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\"><strong>　　架构说明</strong></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"http://127.0.0.1/yiyun/upload/ueditor_img/20181218/1545121178104704.png\" title=\"1545121178104704.png\"/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\">图4:CLOS架构图</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　如图4所示，每块业务线卡和所有交换网板相连，交换芯片集成在交换网板上，实现了交换网板和主控引擎硬件分离。CLOS架构是一种多级架构，每个入口级开关和每个中间级开关之间只有一个连接，并且，每个中间级开关正好连接到每个出口级开关，这种架构的优点是可以通过多个小型Crossbar 开关来实现大量输入和输出端口之间的连接，CrossPoint数量级别低于Crossbar架构的N的2次方，降低了芯片实现难度。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\"><strong>　　报文转发流程</strong></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\"><strong>　　基于Cell的动态负载</strong></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　1. 入<a class=\"keyword_link\" href=\"http://product.yesky.com/chargesystem/direction/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">方向</a>线卡将数据包切分为N个cell，其中：N=下一跳可用线路数量;</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　2. 交换网板采用动态<a class=\"keyword_link\" href=\"http://product.yesky.com/wirelessrouter/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">路由</a>方式，即根据下一级各链路的实际可用交换能力，动态选路和负载均衡，通过多条路径将分片发送到出方向线卡;</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　3. 出方向线卡重组报文。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　动态负载关键点在于能负载分担地均衡利用所有可达路径，由此实现了无阻塞交换。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\"><strong>　　CLOS架构交换机的分类</strong></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\"><strong>　　非正交背板设计</strong></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"http://127.0.0.1/yiyun/upload/ueditor_img/20181218/1545121178116228.png\" title=\"1545121178116228.png\"/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\">图5:非正交背板</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　如图5所示，业务线卡与交换网板互相平行，板卡之间通过背板走线连接。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　背板走线会带来信号干扰，背板设计也限制了带宽的升级，同时，背板上PCB的走线要求很高，从背板开孔就成了奢望，这直接导致纯前后的直通风道设计瓶颈一直无法突破。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\"><strong>　　正交背板设计</strong></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"http://127.0.0.1/yiyun/upload/ueditor_img/20181218/1545121178117950.png\" title=\"1545121178117950.png\"/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\">图6:正交背板</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　如图6所示，交换机线卡与交换网板分别与背板对接。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　同非正交背板设计一样，背板带宽限制了带宽的升级，同时也增加了散热的难度。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\"><strong>　　正交零背板设计</strong></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"http://127.0.0.1/yiyun/upload/ueditor_img/20181218/1545121178185208.png\" title=\"1545121178185208.png\"/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\">图7:正交零背板</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　如图7所示，业务线卡与交换网板互相垂直，背板走线为零，甚至无中板。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　正交设计能减少背板走线带来的高速信号衰减，提高了硬件的可靠性，无背板设计能够解除背板对容量提升的限制，当需要更大带宽的时候，只需要更换相应板卡即可，大大缩短业务升级周期，并且因为没有了背板的限制，交换机直通风道散热问题迎刃而解，完美匹配数据中心机房空气流的走向，形成了贯穿前后板卡的高速、通畅的气流。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\"><strong>　　总结</strong></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　下表将对以上三种架构做出总结：</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"http://127.0.0.1/yiyun/upload/ueditor_img/20181218/1545121178934994.png\" title=\"1545121178934994.png\"/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　对于高端<a class=\"keyword_link\" href=\"http://product.yesky.com/service/rack\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">机架式</a>交换机，以Crossbar交换架构和CLOS交换架构为主。其中CLOS交换架构是当前大容量数据中心核心交换机的理想架构。<strong><a class=\"keyword_link\" href=\"http://product.yesky.com/hardwarefirewall/redgiant/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">锐捷网络</a>RG-N18000-<a class=\"keyword_link\" href=\"http://product.yesky.com/notebookpc/xseries/tcl/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">X系列</a>交换机基于无阻塞的CLOS架构，并且首次采用“零背板”技术，在提供高效、稳定交换服务的同时，可实现未来10年网络可持续平滑升级。</strong></p>', '1', '1590373596');
INSERT INTO `yun_news` VALUES ('58', '4', '物联时代 锐捷极简X助力智慧校园建设', '在本次会议中，锐捷网络与行业媒体共同针对“物联时代高教校园网络如何转型升级”问题，进行了深度探讨。', '../upload/news/up_5c18afa33b3aa.jpg', 'admin', '<p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">12月7日，以“物联时代，极简先行”为主题的高教校园网发展<a class=\"keyword_link\" href=\"http://product.yesky.com/feedsystem/trendnet/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">趋势</a>媒体交流会在北京召开。在本次会议中，<a class=\"keyword_link\" href=\"http://product.yesky.com/hardwarefirewall/redgiant/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">锐捷网络</a>与行业媒体共同针对“物联时代高教校园网络如何转型升级”问题，进行了深度探讨。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　在会议上，<a class=\"keyword_link\" href=\"http://product.yesky.com/switchboard/ruijie/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">锐捷网络交换机</a>事业部产品经理宋子航为现场嘉宾做了专题报告。他表示：“国内高校校园网络经历“扁平化”与“<a class=\"keyword_link\" href=\"http://product.yesky.com/jssesuit/wireless/microsoft/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">无线</a>建设””两个时代的转型升级，现在正在进入数字化系统井喷式接入的物联时代，这些系统极大便利了师生教学与校园生活，但如何承载这些系统，则是摆在学校信息化部门的一个重大难题。”</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　<strong>专注校园物联承载网解决方案 量身定制极简X</strong></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　近几年来，在智慧校园、平安校园、节约型校园等建设理念的落地过程中，国内高校各部门充分利用信息化工具，通过信息化提升学生生活体验、提高老师教学质量和效率。而面对海量物联终端入网，传统构架下的校园网络建设不可避免的会有部门配合效率低，资源复用难，安全溯源能力弱等问题。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　在时代发展和客户需求下，锐捷网络打造了业内首家为物联网“量身定制”的解决方案——极简X。该方案可以从四个维度提升高校校园网的运维效率与用户体验。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"/yiyun/upload/ueditor_img/20181218/1545121569770010.jpg\" title=\"1545121569770010.jpg\" alt=\"4O1HDYOL6V6A.jpg\"/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　这四个维度分别为，不动网入网，自动归类;多业务网，自动隔离;多种服务，灵活提供;可视运维，业绩呈现。也就是说，采用了极简X校园物联承载网解决方案，可在物联终端入网过程中，无需提前规划端口，更改配置;在保证接入、汇聚配置标准化基础上，这些物联终端还能自动归入所属业务。终端上线时间从2天缩短至2小时。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　其次，对于多业务网，基于业务视角的隔离，与端口位置无关，简化部署难度。通过图形化界面，可一“键”隔离。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　此外，极简X物联承载网解决方案还能摆脱被动响应业务部门需求的服务模式，主动地向业务部门提供服务，一个平台集成多种服务，包括;快速接入服务，安全接入服务，分权查看服务，业务监控服务和快速处理服务等。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><a target=\"_blank\" href=\"http://image.tianjimedia.com/uploadImages/2018/347/41/92G7N47GO08T.jpg\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\"><img src=\"http://127.0.0.1/yiyun/upload/ueditor_img/20181218/1545121546893038.jpg\" title=\"1545121546893038.jpg\"/></a></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　极简X物联承载网解决方案还能将IP地址资源使用情况，物联终端在线状态，物联业务数量、终端所在位置、品类、状态可视化业绩呈现。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　迄今为止，极简X已经逐步开始部署与全国14省的多所211、958院校，并在客户需求的驱动下，不断挖掘客户痛点场景，升级方案，铸就极简的创新灵魂。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　<strong>教育与科技接轨 校园网络建设需遵六标准</strong></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　在科技的发展下，教育也需要不断的和科技接轨，那么，在物联时代下的校园网络建设需符合何种标准，如何在物联时代打造一个智慧校园，更有助全校师生的生活和学习。对此，锐捷网络给出了答案。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　教育的最常见场所便是校园，打造一个物联时代下的智慧校园，将可以给所有接受教育的学生带来另一番的体验。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　对于当前校园网建设应该符合哪些标准的问题，锐捷网络<a class=\"keyword_link\" href=\"http://product.yesky.com/switchboard/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">交换机</a>事业部产品经理宋子航表示;“面对用户痛点，我们总结出客户在当前的阶段，校园网建设应该符合哪种标准，这主要表现在6个板块。首先是在整个网络的承载面上，一张网共享所有资源，并且一张网承载多个业务并且业务可以自动隔离;其次，这些终端在入网的时候，能够自动的找到自己所属的业务网络;第三，在准入层面，整个网络当中，要针对物联终端设置一些准入策略;第四，要有溯源的能力，一旦终端出现问题，就能够快速追溯;第五，识别，在学校各个角落的终端到底有多少个，都承载着什么业务，这种情况可以通过一种方式能够全部识别出来。不仅能够可视化管理资产，还能够统一的进行业绩呈现，有助于整个网络中心清晰化的管理;最后就是可视化服务，原来的方式都是被动式的反馈，现在用一种直观的方式感知到终端的在线状态，然后主动的提供服务。”</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　在会上，锐捷网络还展示了物联入网，终端自动识别，准入、可视等特性，充分运用了极简X在数据通信解决方案领域的领先技术优势，扎根校园网场景，挖掘信息部门、业务部门的业务痛点，通过极简X物联承载网方案提供一张可以资源复用、部门间可以高效协作、承载网安全溯源能力强的好网络。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><a target=\"_blank\" href=\"http://image.tianjimedia.com/uploadImages/2018/347/43/9NUX7822333A.jpg\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\"><img src=\"http://127.0.0.1/yiyun/upload/ueditor_img/20181218/1545121546388146.jpg\" title=\"1545121546388146.jpg\"/></a></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　物联时代，极简网络能够在一张网络承载多个业务，实现物理共享，逻辑隔离;所有的终端在入网时，接入网络时可做到自动化入网;校园网络不仅可以上网，还能实现多元化服务，运维也可实现可视化、主动化的服务。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　极简网络的四大价值凸显了三大意义：让原来被忽略上网服务变成被重视的多部门多业务;原来的入网是被动的，现在是智能化的主动服务;以前所有的数据都不可见，现在所有的数据可以进行可视化运营;业务汇报由原来的主观汇报变为客观数据汇报，让整个业绩数据变得更加客观。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><a target=\"_blank\" href=\"http://image.tianjimedia.com/uploadImages/2018/347/43/A5N5151MG8R6.jpg\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\"><img src=\"http://127.0.0.1/yiyun/upload/ueditor_img/20181218/1545121546375667.jpg\" title=\"1545121546375667.jpg\"/></a></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　在跨校区校园网信息集中管理方面，锐捷网络给予的解决方案是给每一个校区都设立一道“闸”(VxLAN分布式<a class=\"keyword_link\" href=\"http://product.yesky.com/wangguan/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">网关</a>)，来避免广播域过大带来的泛洪风险。在多个校区的“闸”上虚拟出来全校物联承载网综合管理网关，承载与学生、老师生活密切相关的一卡通、水、电、节能、教室<a class=\"keyword_link\" href=\"http://product.yesky.com/chargesystem/zksoftware/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">中控</a>、<a class=\"keyword_link\" href=\"http://product.yesky.com/pccamera/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">摄像头</a>、FM调频广播、宣教打铃等各种物联业务，同时以上说的四大价值全部可以实现。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><a target=\"_blank\" href=\"http://image.tianjimedia.com/uploadImages/2018/347/44/P4Z5D3F5E4H1.jpg\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\"><img src=\"http://127.0.0.1/yiyun/upload/ueditor_img/20181218/1545121546181378.jpg\" title=\"1545121546181378.jpg\"/></a></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　宋子航表示，我们的方案来自于调研了众多的211及958高等院校，客户对极简X的评价是<a class=\"keyword_link\" href=\"http://product.yesky.com/rack/eyong/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">易用</a>性、开放性强，客户对极简X方案的落地效果相对来讲还是比较满意的。物联时代，极简网络竭力地在四个方面进行做深度打造，从师生体验、周边部门配合、安全准入效率，以及整体资源使用效率上，都在助力高校通过更好的办法来构建这个时代所需要的校园网。教育发展很快市场很大，未来我们希望为智慧校园做更多的贡献，助力整个教育信息化发展。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><a target=\"_blank\" href=\"http://image.tianjimedia.com/uploadImages/2018/347/40/E54E1562H8T2.jpg\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\"><img src=\"http://127.0.0.1/yiyun/upload/ueditor_img/20181218/1545121546607608.jpg\" title=\"1545121546607608.jpg\"/></a></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　自2016年国家教育部正式提出智慧校园建设四年规划任务以来，“互联网+教育”越来越受到人们的关注，智慧校园建设是教育信息化2.0规划中的重要工作之一。目前，完整的智慧校园虽然还没实际落地，但是很多学校都慢慢的一项一项地改变。相信通过锐捷等企业的技术研发推广，将进一步促进高校信息化水平提升和改善。</p><p><br/></p>', '5', '1590373596');
INSERT INTO `yun_news` VALUES ('59', '4', '淘宝、天猫等APP已用上IPv6 你却还在IPv4的路上奔跑？', '阿里云提供计算服务，IPv6。  　　是的，淘宝APP已经使用IPv6，而且天猫、优酷以及高德等多款用户过亿的应用均成为国内首批落地IPv6技术的APP。', '../upload/news/up_5c18b13b1db02.jpg', 'admin', '<p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">当你打开淘宝APP，如果细心，你会发现一些信息：阿里云提供计算服务，IPv6。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　是的，淘宝APP已经使用IPv6，而且天猫、<a class=\"keyword_link\" href=\"http://mydown.yesky.com/soft/network/download/217/440717.shtml\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">优酷</a>以及<a class=\"keyword_link\" href=\"http://product.yesky.com/gps/gddt\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">高德</a>等多款用户过亿的应用均成为国内首批落地IPv6技术的APP。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　<strong>　阿里巴巴的IPv6之路</strong></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　在IPv6的推广和应用中，阿里巴巴可谓是相当“激进”。从2017年，阿里巴巴就开始投入IPv6技术的研发与应用。截至2018年12月，这家公司已经为超过2000万用户提供IPv6服务，是国内首家率先进行IPv6超大规模应用的科技公司。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"http://127.0.0.1/yiyun/upload/ueditor_img/20181218/1545122024750560.jpg\" title=\"1545122024750560.jpg\"/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　根据阿里官方公布的IPv6布局：</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　2018年6月，阿里云成为国内首家全面提供IPv6服务的云厂商，提供全栈IPv6解决方案，不仅核心产品全面支持IPv6，而且服务超过200个行业场景;</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　同样在2018年6月，优酷在世界杯期间尝试应用IPv6，IPv6用户月活1000万，IPv6流量高达50T/天。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　在双十一期间，阿里旗下的淘宝、天猫、优酷、高德IPv6大规模上线。截至2018年12月，阿里云100%核心产品完成IPv6改造，<a class=\"keyword_link\" href=\"http://mydown.yesky.com/sjsoft/306/35549306.shtml\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">支付宝</a>IPv6大规模上线。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><a target=\"_blank\" href=\"http://image.tianjimedia.com/uploadImages/2018/346/09/ZM8H9SC26BK0.jpg\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\"><img src=\"http://127.0.0.1/yiyun/upload/ueditor_img/20181218/1545122024137082.jpg\" title=\"1545122024137082.jpg\"/></a></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　不久前，阿里巴巴在发布会上宣布已全面应用下一代互联网连接协议，即IPv6。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\"><strong>　　IPv6行动纲领</strong></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　实际上，一年前，中共中央办公厅 国务院办公厅印发《推进互联网协议第六版(IPv6)规模部署行动计划》。“计划”的出台不仅表明政府对IPv6的重视，更是对IPv6发展的鼓励。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　“计划”指出，基于互联网协议第四版(IPv4)的全球互联网面临网络地址消耗殆尽、服务质量难以保证等制约性问题，IPv6能够提供充足的网络地址和广阔的创新空间，是全球公认的下一代互联网<a class=\"keyword_link\" href=\"http://product.yesky.com/scaner/business\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">商业应用</a>解决方案。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　大力发展基于IPv6的下一代互联网，有助于提升我国网络信息技术自主创新能力和产业高端发展水平，高效支撑移动互联网、物联网、工业互联网、云计算、大数据、人工智能等新兴领域快速发展，不断催生新技术新业态，促进网络应用进一步繁荣，打造先进开放的下一代互联网技术产业生态。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　<strong>　从IPv4到IPv6</strong></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　对一般人而言，IPv4用着没问题，为什么要切换到IPv6？</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　IPv4全称是Internet Protocol version 4，即互联网协议第4版;那IPv6则是Internet Protocol version 6，互联网协议第6版。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><a target=\"_blank\" href=\"http://image.tianjimedia.com/uploadImages/2018/346/10/IQ1071KAN90K.jpg\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\"><img src=\"http://127.0.0.1/yiyun/upload/ueditor_img/20181218/1545122024120407.jpg\" title=\"1545122024120407.jpg\"/></a></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　众所周知，网络都是基于协议的，协议就是网络进行运作的规则和标准。举个例子，人和人一般是用语言进行沟通和对话，当沟通没成功，就有人要求对方“说人话”。这里的语言就相当于网络上的协议，协议不同，网络自然成功交换信息。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　我们目前的互联网是基于TCP/IP协议族的，而IP是这个协议族中的核心组成部分。IP是在网络层协议，根据源主机和目的主机的地址，进行数据的传送。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　IPv4已经用了30多年，现在IP地址快耗尽，一是因为互联网的普及，越来越多的人开始接入互联网，但世界总人口数已达75亿左右，但IPv4能提供的有效IP地址仅有43亿个。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　二是随着万物互联时代的到来，更多的<a class=\"keyword_link\" href=\"http://product.yesky.com/kcd/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">智能设备</a>都需要接入网络，这些智能设备都需要IP地址。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　据悉，IPv4一共有42.9亿个，这根本无法满足互联网发展的需要。人们迫切需要更高版本的IP协议，更多数量的IP地址池。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　1998年，IETF(互联网工程小组，成立于1985年，是全球互联网最具权威的技术标准化组织)正式提出IPv6。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><a target=\"_blank\" href=\"http://image.tianjimedia.com/uploadImages/2018/346/09/SV202UQ4459K.jpg\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\"><img src=\"http://127.0.0.1/yiyun/upload/ueditor_img/20181218/1545122024672801.jpg\" title=\"1545122024672801.jpg\"/></a></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　相比IPv4，IPv6的地址数高达2的128次方，这个数字太大。并且，IPv6有很多优势，它具有更高的安全性、更好的扩容能力、更好的头部格式、对自动配置的支持等等。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　<strong>国内IPv6部署：全球排名三十以外</strong></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　虽然IPv6很好，优点多多，但是其进展依旧很慢，主因是利益相关。目前，IP地址主要掌握在电信运营商和互联网服务提供商(ISP)，它们从互联<a class=\"keyword_link\" href=\"http://product.yesky.com/netsecurity/netsys/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">网域</a>名与号码分配机构申请IP地址，然后再卖给下游用户。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　如果升级支持IPv6，电信运营商和ISP需要投入大量的资金，因此它们没有很大的动力去做这件事。结果是，IPv6普及率沦落到全球排名三十以外。真是可悲可叹啊。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　不过，为推动通信基础设备的标准化建设，为5G、物联网、工业4.0打基础和提升我国网络信息技术自主创新能力和产业高端发展水平，国家正不遗余力地推动IPv6发展。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　另一方面，IPv6涉及国家<a class=\"keyword_link\" href=\"http://product.yesky.com/netsecurity/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">网络安全</a>和网络主权。众所周知，上网需要使用DNS<a class=\"keyword_link\" href=\"http://product.yesky.com/service/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">服务器</a>，它的作用是解析域名。比如，我们访问淘宝网，把这个地址发到DNS服务器，服务器返回一个IP地址，你才能访问淘宝网。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><a target=\"_blank\" href=\"http://image.tianjimedia.com/uploadImages/2018/346/10/XMF11ZD2FSX6.jpg\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\"><img src=\"http://127.0.0.1/yiyun/upload/ueditor_img/20181218/1545122024190415.jpg\" title=\"1545122024190415.jpg\"/></a></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　在IPv4阶段，全球DNS根服务器只有13台，1台为主，12台为辅，其中10台DNS根服务器在<a class=\"keyword_link\" href=\"http://product.yesky.com/mobilephone/usa\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">美国</a>，另外3台分别在英国、瑞典和<a class=\"keyword_link\" href=\"http://product.yesky.com/digitalcamera/japan\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">日本</a>。因此，如果想断你的网，切断DNS服务，则是强有力的策略。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　而在IPv6阶段，由下一代互联网国家工程中心发起的“雪人计划”，已完成全球25台IPv6根服务器架设，<a class=\"keyword_link\" href=\"http://product.yesky.com/mobilephone/china\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">中国</a>部署4台，包括1台主根服务器和3台辅根服务器。从某种方面说，网络的安全状况至少得到提高。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　回到文章开头，作为国内知名互联网公司，阿里巴巴对IPv6的大力推进，有助于激励<a class=\"keyword_link\" href=\"http://product.yesky.com/battery/qtdc\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">其他</a>企业使用IPv6，进而推动我国IPv6整体的发展。</p>', '10', '1590373596');
INSERT INTO `yun_news` VALUES ('60', '4', '从李彦宏入围“全球十大AI领军人物”，看百度AI布局', '人工智能技术已成为全球科技巨头关注的焦点之一。', '../upload/news/up_5c18b5193cb1a.jpg', 'admin', '<p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">前言：如今，人工智能技术已成为全球科技巨头关注的焦点之一。作为最早布局人工智能的企业之一，并且是国内人工智能专利申请量排名第一的企业，<a class=\"keyword_link\" href=\"http://mydown.yesky.com/sjsoft/272/35340272.shtml\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">百度</a>在人工智能领域取得了很多突破性的进展。近日，《哈佛商业评论》发布了“全球最受关注的十大AI领军人物”榜单，<a class=\"keyword_link\" href=\"http://product.yesky.com/wirelessrouter/apple/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">苹果</a>公司CEO蒂姆·库克(Tim Cook)、<a class=\"keyword_link\" href=\"http://product.yesky.com/gamessoftware/microsoft\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">微软</a>公司CEO萨提亚·纳德拉(<a class=\"keyword_link\" href=\"http://product.yesky.com/harddisk/sata\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">Satya</a>&nbsp;Nadella)、百度董事长兼CEO李彦宏分获前三名，同时，李彦宏也是榜单中唯一上榜的<a class=\"keyword_link\" href=\"http://product.yesky.com/mobilephone/china\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">中国</a>人。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img alt=\"26J0RHA80F0P.png\" width=\"554\" height=\"342\" src=\"/yiyun/upload/ueditor_img/20181218/1545123057473349.png\" title=\"1545123057473349.png\"/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\">百度创始人、董事长兼CEO李彦宏</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　<strong>李彦宏入选“全球最受关注十大AI领军人物”：唯一的中国面孔!</strong></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　近日，Harvard Business Review(《哈佛商业评论》)发布了“全球最受关注的十大AI领军人物”榜单，榜单前三位分别是苹果公司CEO蒂姆·库克(Tim Cook)、微软CEO萨提亚·纳德拉(Satya Nadella)、百度董事长兼CEO李彦宏，尤其值得一提的是，李彦宏是本次榜单唯一入选的中国人。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　《哈佛商业评论》对李彦宏的评价是：“中国人工智能行业的设计师，他提出了企业要实现“人工智能化”需要做到三位一体，即具备AI思维、提升AI能力、遵循AI伦理;百度推出了全球首个无人驾驶开放平台，推动了智能驾驶产业的发展;他是中国唯一拥有全套人工智能技术和产业能力的CEO，在他的版图内，人工智能对于产业升级的推动已逐渐成熟并进入落地应用阶段。”</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"http://127.0.0.1/yiyun/upload/ueditor_img/20181218/1545123057122950.jpg\" title=\"1545123057122950.jpg\"/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\">“全球最受关注的十大AI领军人物”榜单</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　《哈佛商业评论》表示，这些企业家之所以入选，并不是他们拥有全球科技最高技术能力，而是他们通过思想、洞察力、行动，对公司以及行业产生了实际影响力以及起到了关键性作用。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　<strong>百度人工智能的发展历程</strong></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　百度是中国最早在人工智能领域进行布局的科技巨头之一，在人工智能的驱动下，百度核心业务增长明显。百度在人工智能产品化、商业化方面已经取得不小的进展。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　早在2010年，百度就开始利用人工智能技术，使用机器学习重新调整百度的搜索算法，并先后进行了自然语言处理、机器翻译、数据挖掘等技术的研发工作;2013年1月，百度成立了深度学习研究院;2013年4月，百度建立了人工智能实验室;2015年5月，百度上线了世界上首个神经网络翻译(NMT)系统;2016年9月，百度大脑正式发布，并对外开放人工智能核心技术，目前百度大脑已经更新到3.0版本。2017年4月，百度发布“阿波罗计划”，宣布将向汽车行业提供开放的自动驾驶软硬件平台以及服务体系。2018年7月，百度发布中国首款云端全功能AI芯片“昆仑”。2018年11月，百度与一汽共同推出国内首款L4级别自动驾驶乘用车，并宣布了量产计划;2018年11月，百度推出了AI城市“ACE王牌计划”，该计划将以自动驾驶、车路协同、智能城市为发展脉络，坚守智能化、自动化，让人工智能走进城市的每一个角落。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"http://127.0.0.1/yiyun/upload/ueditor_img/20181218/1545123057589105.png\" title=\"1545123057589105.png\"/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\">百度与一汽红旗共同研发的L4级自动驾驶乘用车</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　目前，百度人工智能产品已经开始显现其规模效应。在产业层面，百度人工智能成果已经覆盖国民经济三大产业，包括自动驾驶、<a class=\"keyword_link\" href=\"http://product.yesky.com/smarthome/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">智能家居</a>、智慧城市建设等多个领域。在工业领域，百度发布了中国首款L4级自动驾驶乘用车，并推出了世界上第一台无人自主挖掘机;在第三产业，人工智能在零售、医疗等众多领域都实现了落地应用。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　在人工智能领域，百度表现出了超高的潜力和实力，为什么百度在人工智能领域能够取得如此大的进展呢？</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　<strong>　百度在人工智能领域具备哪些优势?</strong></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　1、强大的技术团队</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　根据《人工智能技术专利深度分析报告》显示，百度以2368件申请量在国内申请人中位列第一，这其中离不开人才的贡献。近年来，百度在人工智能领域的投入力度以及日渐清晰的战略规划，让百度成为大量海外人工智能高端人才归国发展的首要之选。所以，在推动人工智能产业发展的过程中，人工智能人才将成为百度人工智能战略落地以及产业发展的重要力量。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　2、海量的数据积累</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　人工智能的发展离不开大数据的支持。作为人工智能雏形的搜索引擎业务，它可以称得上是开启人工智能大门的钥匙。百度作为依靠搜索功能逐渐发展壮大的科技公司，拥有强大的数据采集能力以及分析能力，而人工智能是要基于大数据分析，结合机器学习，才能不断的优化。所以，百度在人工智能领域将有很好的发展。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　<strong>写在最后：</strong></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　通过百度在人工智能领域的布局，我们可以看出，如果想要走在科技前沿，除了要具备长远的战略眼光，还要重视人才的引进，注重技术研发和数据积累。未来，百度在人工智能领域还将爆发出怎样的能量，让我们拭目以待。</p><p><br/></p>', '1', '1590373596');
INSERT INTO `yun_news` VALUES ('61', '4', '路由器秒变成个人私有云存储', '这款路由器有一个独立的收纳空间，可以收纳目前主流的：希捷、西数、日立、联想、爱国者等', '../upload/news/up_5c18b6495a7aa.png', 'admin', '<p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-align: left; text-indent: 2em;\">今天给大家介绍一款中高端<a class=\"keyword_link\" href=\"http://product.yesky.com/router/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">路由器</a>，但是他不仅仅是一台路由器。</p><p style=\"text-align: center;\"><img src=\"/yiyun/upload/ueditor_img/20181218/1545123314137236.jpg\" title=\"1545123314137236.jpg\"/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-align: justify;\">　　这款路由器有一个独立的收纳空间，可以收纳目前主流的：希捷、西数、日立、联想、<a class=\"keyword_link\" href=\"http://product.yesky.com/smarthome/aigo/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">爱国者</a>等，<a class=\"keyword_link\" href=\"http://product.yesky.com/ssd/25yc\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">2.5英寸</a>以下的<a class=\"keyword_link\" href=\"http://product.yesky.com/mobileharddisk/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">移动硬盘</a>，接口方面无论是<a class=\"keyword_link\" href=\"http://product.yesky.com/flashmemory/usb20\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">USB2.0</a>、<a class=\"keyword_link\" href=\"http://product.yesky.com/flashmemory/usb30\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">USB3.0</a>还是type-c接口都可以使用。即使是用户使用<a class=\"keyword_link\" href=\"http://product.yesky.com/mobileharddiskbox/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">硬盘盒</a>自行组装的移动<a class=\"keyword_link\" href=\"http://product.yesky.com/harddisk/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">硬盘</a>也可以。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"http://127.0.0.1/yiyun/upload/ueditor_img/20181218/1545123314643023.png\" title=\"1545123314643023.png\"/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-align: justify;\">　　将移动硬盘放入收纳空间内，连接好<a class=\"keyword_link\" href=\"http://product.yesky.com/earphone/usb\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">USB</a>线，用标配的捆绑带固定，简单的三步就可以组建专属个人私有云存储设备了。组建好个人云存储，下载<a class=\"keyword_link\" href=\"http://product.yesky.com/mobilephone/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">手机</a>客户端、<a class=\"keyword_link\" href=\"http://product.yesky.com/gamessoftware/pc\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">PC</a>客户端，通过袋鼠云<a class=\"keyword_link\" href=\"http://product.yesky.com/wirelessrouter/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">路由</a>通过的软件服务实现随时随地对硬盘内的数据进行访问、下载、备份、共享等多种功能，小编感觉收纳的设计的很用心，很方便。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"http://127.0.0.1/yiyun/upload/ueditor_img/20181218/1545123314461644.jpg\" title=\"1545123314461644.jpg\"/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　这样的产品创新，将<a class=\"keyword_link\" href=\"http://product.yesky.com/mic/cable\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">有线</a>的移动硬盘变成<a class=\"keyword_link\" href=\"http://product.yesky.com/jssesuit/wireless/microsoft/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">无线</a>移动硬盘会不会成为一种趋势？我们拭目以待。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-align: justify;\">　　另外从路由器的规格来看，载128M的<a class=\"keyword_link\" href=\"http://product.yesky.com/memory/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">内存</a>+64M的闪存可以让用户流畅的访问硬盘中的照片与视频，最大限度保证用户体验的稳定性与流畅度。我们的软件团队对照片、视频的读取的算法上进行优化，推出了缩略图的机制，大幅提高了浏览时的流畅度与矢量图的显示效果。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-align: justify;\">　　信号方面，2.<a class=\"keyword_link\" href=\"http://product.yesky.com/mouse/4g\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">4G</a>+5.8G<a class=\"keyword_link\" href=\"http://product.yesky.com/wirelessrouter/double\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">双频</a>1200M的规格，满足手机<a class=\"keyword_link\" href=\"http://product.yesky.com/speaker/wifi\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">WiFi</a>接入后自动备份照片的使用需求。在5.8G的频段的<a class=\"keyword_link\" href=\"http://product.yesky.com/ceshi/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">测试</a>显示：2000张手机高清照片只需要5-8分钟的时间。未来5.8G的频道会成为手机、<a class=\"keyword_link\" href=\"http://product.yesky.com/lcdtv/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">电视</a>等WiFi终端设备的标配，而5.8G的路由器也将慢慢普及。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"http://127.0.0.1/yiyun/upload/ueditor_img/20181218/1545123314954530.jpg\" title=\"1545123314954530.jpg\"/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-align: justify;\">　　<a class=\"keyword_link\" href=\"http://product.yesky.com/feedsystem/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">天线</a>的设计也别具一格，告别了圆柱形的天线，改为扁平圆头的外形设计、可以自由旋转。天线内部共有四根天线，2.4G两根、5.8G两根，为了满足大户型对信号的要求，配置了：PA+LAN的信号增强模块，这样的组合被认为是<a class=\"keyword_link\" href=\"http://product.yesky.com/wirelessrouter/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">无线路由器</a>的信号的黄金组合，有了这样的黄金组合，大幅提高路由的穿墙能力，无论是上网、游戏、数据备份都游刃有余。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"http://127.0.0.1/yiyun/upload/ueditor_img/20181218/1545123313427994.jpg\" title=\"1545123313427994.jpg\"/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 5px 15px; border: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; background-color: rgb(255, 255, 255);\">　　目前这款号称业界创意第一的袋鼠云路由已在京东发起众筹，想了解这款具有前沿探索意识的路由用户不妨多多留意一下。</p><p><br/></p>', '2', '1590373666');
INSERT INTO `yun_news` VALUES ('62', '4', '软博会', '技术特征', '../upload/news/up_5ecbb98c115dd.jpg', 'admin', '<p style=\"margin-top: 0px; margin-bottom: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; padding: 5px 15px; border: 0px; background-color: rgb(255, 255, 255);\">提起开发者，你会想到什么？年轻、机智、冷静、直率、自我、蓝色牛仔裤等等。在一般人眼里，开发者或许等同于程序员、技术宅，它们的工作也显得“高深莫测”。</p><p style=\"margin-top: 0px; margin-bottom: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; padding: 5px 15px; border: 0px; background-color: rgb(255, 255, 255);\">　　越来越多的新技术、新方向驱动产业、个人生活不断科技化，神秘的开发者不再稀有。在传统产业的科技化转型中，开发者群体不断壮大，这也给<a class=\"keyword_link\" href=\"http://product.yesky.com/ultrabook/jingdian\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">普通</a>人了解他们打开了一扇窗。</p><p style=\"margin-top: 0px; margin-bottom: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; padding: 5px 15px; border: 0px; background-color: rgb(255, 255, 255);\">　　众所周知，全球知名的科技互联网公司，比如<a class=\"keyword_link\" href=\"http://product.yesky.com/gamessoftware/microsoft\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">微软</a>、谷歌、Facebook每年都会召开开发者大会，不仅吸引全球各地的开发者参与，而且阐述公司的未来愿景。无疑，开发者是真正改变世界的一股力量。</p><p style=\"margin-top: 0px; margin-bottom: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; padding: 5px 15px; border: 0px; background-color: rgb(255, 255, 255);\">　　相比国外的开发者社区、群体，国内开发者却很低调。那么，国内开发者现状如何？</p><p style=\"margin-top: 0px; margin-bottom: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; padding: 5px 15px; border: 0px; background-color: rgb(255, 255, 255);\">　　日前，《云栖社区》发布了“云栖社区2017<a class=\"keyword_link\" href=\"http://product.yesky.com/mobilephone/china\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">中国</a>开发者调查报告”，得以让大家一窥中国开发者现状。该报告从三个层面(技术特征、社会特征和个人特征)描述了中国开发者现状。</p><p style=\"margin-top: 0px; margin-bottom: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; padding: 5px 15px; border: 0px; background-color: rgb(255, 255, 255);\">　<strong>　一.技术特征</strong>。该报告涵盖了开发环境、开发语言、编程环境、<a class=\"keyword_link\" href=\"http://product.yesky.com/database/data\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">数据库</a>、代码托管平台等11个方面。目前，<a class=\"keyword_link\" href=\"http://product.yesky.com/os/windows\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">Windows</a>以67.2%高居开发环境榜首，其次是<a class=\"keyword_link\" href=\"http://product.yesky.com/notebookpc/mac\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">MacOS</a>和<a class=\"keyword_link\" href=\"http://product.yesky.com/os/linux\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">Linux</a>。对中国开发者来说，Windows依然是受众最多的<a class=\"keyword_link\" href=\"http://product.yesky.com/os/\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">操作系统</a>，无论是在平台的开放性、平台资源、开发者的熟悉程度和商业模式方面，Windows都具备非常明显的优势。</p><p style=\"margin-top: 0px; margin-bottom: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; padding: 5px 15px; border: 0px; background-color: rgb(255, 255, 255);\">　　而开发语言的使用，JavaScript位居第一，其次是<a class=\"keyword_link\" href=\"http://mydown.yesky.com/soft/442/34540942.shtml\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">Java</a>和SQL。毫无疑问，JavaScript是中国开发者最喜欢的语言。在中国开发者应用最多的前五个开发语言中，多数开发者收入稳定在1万人民币到2万人民币之间，但Java、<a class=\"keyword_link\" href=\"http://mydown.yesky.com/soft/133/33506133.shtml\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">Python</a>的高收入群体比例明显提升，这也可以部分解释为何Java受到中国开发者青睐。</p><p style=\"margin-top: 0px; margin-bottom: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; padding: 5px 15px; border: 0px; background-color: rgb(255, 255, 255);\">　　在代码托管平台方面，GitHub以30.7%排名第一，其次是公司内部工具和GitLab。</p><p style=\"margin-top: 0px; margin-bottom: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; padding: 5px 15px; border: 0px; background-color: rgb(255, 255, 255);\">　<strong>　二.社会特征。</strong>开发者所属城市，北京、杭州和上海成为TOP3，值得注意的是杭州成为仅次于北京的开发者城市。而在西部区域，成都、西安分别成为开发者最喜欢的两大城市，但是以这两个城市为边界的更西部，则成为了开发者的“沙漠”区域。</p><p style=\"margin-top: 0px; margin-bottom: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; padding: 5px 15px; border: 0px; background-color: rgb(255, 255, 255);\">　　在所属行业方面，软件服务依然以40%高居榜首，其次是信息通讯和零售。而在企业规模上，中国大量的开发者主要供职于人员规模在1000人以下的中<a class=\"keyword_link\" href=\"http://product.yesky.com/wirelessrouter/xxqy\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">小型企业</a>当中，仅有23.5%的开发者为中国的<a class=\"keyword_link\" href=\"http://product.yesky.com/wirelessap/dxqy\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">大型企业</a>服务。</p><p style=\"margin-top: 0px; margin-bottom: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; padding: 5px 15px; border: 0px; background-color: rgb(255, 255, 255);\">　　然后是沟通工具。在中国，有66.2%的开发者选择“国民应用”<a class=\"keyword_link\" href=\"http://mydown.yesky.com/sjsoft/299/11799799.shtml\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">微信</a>作为沟通工具，44.1%的开发者选择<a class=\"keyword_link\" href=\"http://mydown.yesky.com/sjsoft/340/46828340.shtml\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">钉钉</a>作为主要的沟通工具。</p><p style=\"margin-top: 0px; margin-bottom: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; padding: 5px 15px; border: 0px; background-color: rgb(255, 255, 255);\">　　<strong>三.个人特征。</strong>在受教育状况上，有一半以上(58.6%)的开发者为大学本科教育，然后是专科和硕士。根据报告解释，多数开发者所具备的学历集中在本科及以上水平，这一比例高达71.9%。显然，中国开发者的平均受教育程度高于全球的平均水平。</p><p style=\"margin-top: 0px; margin-bottom: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; padding: 5px 15px; border: 0px; background-color: rgb(255, 255, 255);\">　　另一方面，“高薪”也是中国开发者的重要特征。87.4%的中国开发者月收入水平高于5000，67.6%的开发者月收入达到<a class=\"keyword_link\" href=\"http://product.yesky.com/chukongyitiji/8000up\" target=\"_blank\" style=\"color: rgb(10, 80, 161); text-decoration-line: none;\">8000元以上</a>。</p><p style=\"margin-top: 0px; margin-bottom: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 24px; font-family: 宋体; white-space: normal; padding: 5px 15px; border: 0px; background-color: rgb(255, 255, 255);\">　　不过，“女性”开发者在中国依然是稀有物种。根据本次的报告，仅有7.9%的女性从事开发者工作，这一比例与2017年Stack Overflow全球开发者调研中的结果接近。而在开发技术领域方面，Web开发、前端开发、云计算、大数据、人工智能、移动开发、区块链和安全成为最突出的八大特征。</p><p><br/></p>', '15', '1590373596');

