-- ----------------------------
-- Table structure for `yun_product`
-- ----------------------------
DROP TABLE IF EXISTS `yun_product`;
CREATE TABLE `yun_product` (
  `ProductID` int(11) unsigned NOT NULL AUTO_INCREMENT,
  `ColumnID` int(11) DEFAULT NULL,
  `ProductName` varchar(255) DEFAULT NULL COMMENT '产品名称',
  `ProductUrl` varchar(255) DEFAULT NULL COMMENT '产品图片地址',
  `ProductInfo` text COMMENT '详细信息、内容',
  PRIMARY KEY (`ProductID`)
) ENGINE=MyISAM AUTO_INCREMENT=30 DEFAULT CHARSET=utf8;

