-- Records of yun_product
-- ----------------------------
INSERT INTO `yun_product` VALUES ('1', '3', '锐捷网络RG-IOTP软件版', '../upload/product/up_5c17105d79ffa.jpg', '<p>参考价格：</p><p><span style=\"padding: 0px; margin: 0px 16px 0px 0px; border: 0px none; font-family: 微软雅黑; font-size: 20px; color: rgb(255, 54, 0); float: left;\">暂无报价</span><a target=\"_self\" style=\"color: rgb(10, 80, 161); font-family: 宋体; display: inline-block;\">价格纠错&gt;&gt;</a></p><p><br/></p><ul style=\"padding: 0px 0px 0px 1px; border: 0px none; height: 72px; overflow: hidden;\" class=\" list-paddingleft-2\"><li><p>软件类型：云管理平台</p></li><li><p>产品亮点：RG-IOTP 锐捷物联网平台软件版本，需要部署服务器、操作系统。适用于用户规模较大的场景。</p></li><li><p>产品概述：是一款软硬件一体化产品，</p></li></ul><p><br/></p><p><span style=\"font-size: 24px;\">产品简介：</span><span style=\"color: rgb(80, 80, 80); font-family: Arial, 宋体; font-size: 20px;\">锐捷网络RG-IOTP软件版，是一款软硬件一体化产品，部署简便、成本低，上电即用，无需购买和部署服务器、操作系统、数据库，有效地提高部署效率，缩短部署工期，节约实施成本。适用于用户规模中小的场景。</span></p><p><span style=\"color: rgb(80, 80, 80); font-family: Arial, 宋体; font-size: 20px;\"><br/></span></p><p><span style=\"color: rgb(80, 80, 80); font-family: Arial, 宋体; font-size: 20px;\"><br/></span></p><p><span style=\"color: rgb(80, 80, 80); font-family: Arial, 宋体; font-size: 20px;\"></span></p><p><br/></p><table class=\"param-a\" width=\"765\"><tbody><tr class=\"firstRow\"><td class=\"zycs\" style=\"padding: 0px;\"><br/></td></tr></tbody></table><p><span style=\"font-size: 24px;\"><span style=\"color: rgb(84, 141, 212); font-size: 24px;\">主要参数</span><span style=\"color: rgb(84, 141, 212); font-size: 24px;\"></span></span></p><table class=\"paramtable-b\" width=\"765\"><tbody><tr class=\"firstRow\"><th style=\"border-color: rgb(205, 227, 253); overflow-wrap: break-word; word-break: break-all; font-weight: normal; width: 110px; height: 34px; background: rgb(229, 241, 255); color: rgb(83, 110, 142); text-align: right;\">软件类型</th><td style=\"border-color: rgb(205, 227, 253); overflow-wrap: break-word; word-break: break-all; padding-right: 6px; line-height: 24px;\" width=\"245\">云管理平台</td><th style=\"border-color: rgb(205, 227, 253); overflow-wrap: break-word; word-break: break-all; font-weight: normal; width: 110px; height: 34px; background: rgb(229, 241, 255); color: rgb(83, 110, 142); text-align: right;\">产品亮点</th><td style=\"border-color: rgb(205, 227, 253); overflow-wrap: break-word; word-break: break-all; padding-right: 6px; line-height: 24px;\" width=\"245\">RG-IOTP 锐捷物联网平台软件版本，需要部署服务器、操作系统。适用于用户规模较大的场景。</td></tr></tbody></table><table class=\"paramtable-a\" width=\"765\"><tbody><tr class=\"firstRow\"><th style=\"width: 104px; font-family: 微软雅黑, 黑体; font-size: 22px; font-weight: normal; color: rgb(10, 80, 161); text-align: left; padding: 10px 15px 12px; line-height: 26px; white-space: nowrap;\">基本参数</th><td style=\"padding: 12px 0px 6px;\" width=\"629\"><br/></td></tr></tbody></table><table class=\"paramtable-b\" width=\"765\"><tbody><tr class=\"firstRow\"><th style=\"border-color: rgb(205, 227, 253); overflow-wrap: break-word; word-break: break-all; font-weight: normal; width: 110px; height: 34px; background: rgb(229, 241, 255); color: rgb(83, 110, 142); text-align: right;\">软件类型</th><td style=\"border-color: rgb(205, 227, 253); overflow-wrap: break-word; word-break: break-all; padding-right: 6px; line-height: 24px;\" width=\"245\">云管理平台</td><th style=\"border-color: rgb(205, 227, 253); overflow-wrap: break-word; word-break: break-all; font-weight: normal; width: 110px; height: 34px; background: rgb(229, 241, 255); color: rgb(83, 110, 142); text-align: right;\">产品概述</th><td style=\"border-color: rgb(205, 227, 253); overflow-wrap: break-word; word-break: break-all; padding-right: 6px; line-height: 24px;\" width=\"245\">是一款软硬件一体化产品，部署简便、成本低，上电即用，无需购买和部署服务器、操作系统、数据库，有效地提高部署效率，缩短部署工期，节约实施成本。适用于用户规模中小的场景</td></tr><tr><th style=\"border-color: rgb(205, 227, 253); overflow-wrap: break-word; word-break: break-all; font-weight: normal; width: 110px; height: 34px; background: rgb(229, 241, 255); color: rgb(83, 110, 142); text-align: right;\">产品亮点</th><td style=\"border-color: rgb(205, 227, 253); overflow-wrap: break-word; word-break: break-all; padding-right: 6px; line-height: 24px;\" width=\"245\">RG-IOTP 锐捷物联网平台软件版本，需要部署服务器、操作系统。适用于用户规模较大的场景。</td><th style=\"border-color: rgb(205, 227, 253); overflow-wrap: break-word; word-break: break-all; font-weight: normal; width: 110px; height: 34px; background: rgb(229, 241, 255); color: rgb(83, 110, 142); text-align: right;\">&nbsp;</th><td style=\"border-color: rgb(205, 227, 253); overflow-wrap: break-word; word-break: break-all; padding-right: 6px; line-height: 24px;\" width=\"245\"><br/></td></tr></tbody></table><p><br/></p><p style=\"white-space: normal;\">在淘宝可以购买！<a href=\"https://www.taobao.com/\" target=\"_self\">点击跳转</a>&nbsp;</p><p style=\"white-space: normal;\">如需在线购买的话。请留言，我们会稍后联系你，敬请等候<img src=\"http://img.baidu.com/hi/jx2/j_0076.gif\"/></p><p><br/></p>');
INSERT INTO `yun_product` VALUES ('2', '3', 'DRP移动电商管理互联网+管理软件', '../upload/product/up_5c1706b5c0306.jpg', '<p><span style=\"font-size: 20px;\"><strong><span style=\"color: rgb(102, 102, 102); font-family: Tahoma, Arial, 宋体, sans-serif; background-color: rgb(255, 255, 255);\">详细说明</span></strong></span></p><p><span style=\"font-size: 20px;\"><strong><span style=\"color: rgb(102, 102, 102); font-family: Tahoma, Arial, 宋体, sans-serif; background-color: rgb(255, 255, 255);\"><br/></span></strong></span></p><p><span style=\"font-size: 18px;\"><strong><span style=\"color: rgb(102, 102, 102); font-family: Tahoma, Arial, 宋体, sans-serif; background-color: rgb(255, 255, 255);\"><strong style=\"font-size: 18px; white-space: normal;\">产品特点：</strong></span></strong></span></p><p style=\"text-indent: 2em;\"><span style=\"text-indent: 2em;\">采购管理、销售管理、库存管理、往来管理、财务管理、借欠业务&nbsp;&nbsp;</span></p><p style=\"text-indent: 2em;\"><span style=\"text-indent: 2em;\">进销存标准版是针对中小型单一运作企业而开发的管理软件，集进销存、财务、分销、OA管理一体化，核心功能集中包括：采购管理、仓储管理、销售管理以及基本的往来和财务管理，凭借操作简单、方便快捷的优势，能够帮助企业快速梳理自己的业务流程。软件沿承了伟博系列产品实用</span></p><p style=\"text-indent: 2em;\"><span style=\"text-indent: 2em;\"><br/></span></p><p style=\"text-align: left; text-indent: 0em;\"><span style=\"font-size: 18px;\"><strong>产品特点：</strong></span> &nbsp; &nbsp;&nbsp;</p><p style=\"text-indent: 2em;\">采用伟博软件公司自主研发的平台开发，体现了“智能化，人性化，软件适应企业”的设计理念。极大地拓展了软件功能应用的空间。</p><p style=\"text-indent: 2em;\">采用B/B结构设计，客户端通过Web浏览器登录服务器，只要上网就可以进行业务处理。系统成熟而稳定，处于国内领先地位。</p><p style=\"text-indent: 2em;\"><span style=\"text-indent: 2em;\">“傻瓜财务”理念，操作简单，只需录入通俗易懂的原始业务凭证，系统就自动编制记账凭证、分类、汇总，生成各类财务报表，做到懂生意就能管账。</span></p><p style=\"text-indent: 2em;\"><span style=\"text-indent: 2em;\">多级审核管理控制运营风险，所有单据支持审核功能，由用户制定审核流程、审核级次、审核原因，以规模企业业务流程，降低企业运营风险。</span></p><p style=\"text-indent: 2em;\">&nbsp;强大的销售报表分析及进销存查询功能、断码查询功能，实时的滞畅分析、业务分析，让企业及时做<span style=\"text-indent: 2em;\">库存报警、断码报警防止积压与缺货，随时掌握库存情况。出经营调整。</span></p><p><br/></p><p><br/></p><p style=\"white-space: normal;\">在淘宝可以购买！<a href=\"https://www.taobao.com/\" target=\"_self\">点击跳转</a>&nbsp;</p><p style=\"white-space: normal;\">如需在线购买的话。请留言，我们会稍后联系你，敬请等候<img src=\"http://img.baidu.com/hi/jx2/j_0076.gif\"/></p><p><br/></p>');
