INSERT INTO `yun_product` VALUES ('11', '3', '科来网络分析系统 专家版', '../upload/product/up_5c1719c3c12a6.jpg', '<ul class=\"gbckjg list-paddingleft-2\" style=\"padding: 0px; margin: 0px; border: 0px none; float: left; width: 426px;\"><li><p>参考价格：</p></li><li><p><span style=\"padding: 0px; margin: 0px 16px 0px 0px; border: 0px none; font-family: 微软雅黑; font-size: 20px; color: rgb(255, 54, 0); float: left;\">即将上市</span><a target=\"_self\" style=\"color: rgb(10, 80, 161); font-family: 宋体; display: inline-block;\">价格纠错&gt;&gt;</a></p></li></ul><p style=\"white-space: normal;\"><br/></p><p style=\"white-space: normal;\"><br/></p><p style=\"white-space: normal;\"><br/></p><p style=\"white-space: normal;\"><br/></p><p style=\"white-space: normal;\">产品简介：<span style=\"color: rgb(80, 80, 80); font-family: Arial, 宋体; font-size: 12px;\">&nbsp;<span style=\"color: rgb(80, 80, 80); font-size: 16px; font-family: 宋体, SimSun;\">科来网络分析系统 专家版的功能特征：全局到节点的网络流量统计； 了解流量应用组成以及如何被利用； 监测网络带宽利用率； 网络故障自动诊断，提供故障定位； 捕获网络数据包、检测网络传输的所有数据； 自动发现IP、端口、主机会话和物理端点； 监测伪造的IP，找到病毒感染主机或攻击源； 监测内网web访问情况； 监测内网电子邮件收发情况； 监测内网FTP文件传输内容； 提供数据过滤与筛选，来调节检测范围； 数据包解码分析、深入的数据分析； 彩色协议树拓展、网络应用分析； TCP数据流重建、跟踪数据传输过程； 监测网络连接情况、找出会话最大的主机； 监测网络错误，进行网络错误统计和定位； 详细的报表和日志记录； 提供各种统计分析图表、历史采样；快照记录网络历史数据、提供数据参照； 检测潜在安全漏洞、为安全防御提供决策依据； 检测网络内的伪造数据，查找攻击源； 故障高清晰分析，查找问题根源。</span></span></p><p style=\"white-space: normal;\"><br/></p><p style=\"white-space: normal;\"><br/></p><ul class=\"paramindex list-paddingleft-2\" style=\"padding: 0px; margin: 0px 0px 16px; border: 1px solid rgb(205, 227, 253); width: 1000px; font-family: 微软雅黑, 黑体; font-size: 14px; overflow: hidden; white-space: normal;\"></ul><table class=\"param-a\" width=\"765\"><tbody><tr class=\"firstRow\"><td class=\"zycs\" style=\"padding: 0px;\"><br/></td></tr></tbody></table><p><span style=\"font-size: 24px;\"><span style=\"color: rgb(84, 141, 212); font-size: 24px;\">主要参数</span><span style=\"color: rgb(84, 141, 212); font-size: 24px;\"></span></span></p><table class=\"paramtable-b\" 